import React, { useEffect, useState } from "react";
import axios from "axios";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    // Fetch candidates from the backend
    const fetchCandidates = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/candidates");
        setCandidates(response.data);
      } catch (error) {
        console.error("Error fetching candidates:", error);
      }
    };

    fetchCandidates();
  }, []);

  const handleVote = async (candidateName) => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/vote",
        { candidateName }, // Send only candidateName
        { timeout: 30000 } // Increased timeout to 30s
      );

      if (!response.data.success) throw new Error("Vote not recorded!");

      alert("Vote cast successfully!");
    } catch (error) {
      console.error("Error voting:", error);
      alert(`Failed to cast vote. Try again. \nReason: ${error.message}`);
    }
  };

  return (
    <div>
      <h2>Vote for Your Candidate</h2>
      {candidates.length === 0 && <p>Loading candidates...</p>}

      {candidates.map((candidate, index) => (
        <div key={index} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
          <img src={candidate.candidateLogo} alt={candidate.candidateName} width="100" height="60" />
          <button onClick={() => handleVote(candidate.candidateName)} style={{ marginLeft: "10px" }}>
            Vote
          </button>
        </div>
      ))}
    </div>
  );
};

export default VoterDashboard;
