                                console.error(`Error: ${stderr}`);
                                return res.status(500).json({ message: 'Face recognition failed' });
                            }
                
                            if (stdout.trim() === 'MATCH') {
                                res.json({ message: 'Face recognized. Access granted' });
                            } else {
                                res.status(401).json({ message: 'Face does not match' });
                            }
                        });
                        res.json(user);
                // Redirect to voter dashboard
                navigate('/voter-dashboard');
            } else {
                setError('Invalid voter ID. Please try again.');
            }
        }
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <input
                            type="text"
                            placeholder="Enter ID"
                            value={id}
                            onChange={(e) => setId(e.target.value)}
                            required
                        />
                    </div>
                    {error && <div className="error-message">{error}</div>}
                    <button type="submit" className="login-button">
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Login;
