import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [selectedCandidate, setSelectedCandidate] = useState(null);
  const voterId = "456789123025"; // ✅ Hardcoded for testing

  // ✅ Function to verify if the voter is valid and check if they have voted
  const verifyVoter = useCallback(async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/voter/check-vote", { voter_id });

      if (response.data.success) {
        if (response.data.message.includes("Thanks for voting")) {
          setHasVoted(true);
        } else {
          setIsVerified(true);
          fetchCandidates();
        }
      } else {
        alert("❌ Invalid Voter ID.");
      }
    } catch (error) {
      console.error("❌ Error verifying voter:", error);
      alert("Error verifying voter. Check server logs.");
    }
  }, [voterId]);

  // ✅ Fetch candidates after verifying voter
  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(response.data);
    } catch (error) {
      console.error("❌ Error fetching candidates:", error);
    }
  };

  useEffect(() => {
    verifyVoter();
  }, [verifyVoter]); // ✅ Fix useEffect dependency warning

  // ✅ Handle vote submission
  const handleVote = async () => {
    if (!selectedCandidate) {
      alert("⚠️ Please select a candidate first!");
      return;
    }

    try {
      const { _id, name } = selectedCandidate;
      const response = await axios.post("http://localhost:5000/api/voter/vote", {
        voterId,
        candidateId: _id,
        candidateName: name,
      });

      if (response.data.success) {
        alert("🎉 Vote cast successfully!");
        setHasVoted(true); // ✅ Update state after voting
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      console.error("❌ Error voting:", error.response?.data || error.message);
      alert("Error casting vote.");
    }
  };

  return (
    <div className="voter-dashboard">
      <h2>🗳 Voter Dashboard</h2>

      {hasVoted ? (
        <h3>✅ Thanks for Voting!</h3>
      ) : isVerified ? (
        <div>
          <h3>Select a Candidate:</h3>
          {candidates.length === 0 ? (
            <p>Loading candidates...</p>
          ) : (
            <div className="candidates-container">
              {candidates.map((candidate) => (
                <div
                  key={candidate._id}
                  className={`candidate-card ${selectedCandidate?._id === candidate._id ? "selected" : ""}`}
                  onClick={() => setSelectedCandidate(candidate)}
                >
                  <h3>{candidate.name} ({candidate.party})</h3>
                  <img src={`http://localhost:5000/uploads/${candidate.logo}`} alt={candidate.name} />
                </div>
              ))}
            </div>
          )}
          <button onClick={handleVote} disabled={!selectedCandidate}>Vote</button>
        </div>
      ) : (
        <p>Verifying Voter ID...</p>
      )}
    </div>
  );
};

export default VoterDashboard;
