import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(false); // ‚úÖ Track if voter has already voted
  const [selectedCandidate, setSelectedCandidate] = useState(null);
  const voterId = "456789123025"; // ‚úÖ Hardcoded for testing

  useEffect(() => {
    verifyVoter();
  }, []);

  const verifyVoter = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/voter/check-voter", { voter_id: voterId });

      if (response.data.success) {
        if (response.data.alreadyVoted) {
          setHasVoted(true); // ‚úÖ Set state to show "Thanks for Voting" message
        } else {
          setIsVerified(true);
          fetchCandidates();
        }
      } else {
        alert("‚ùå Invalid Voter ID.");
      }
    } catch (error) {
      console.error("‚ùå Error verifying voter:", error);
    }
  };

  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(response.data);
    } catch (error) {
      console.error("‚ùå Error fetching candidates:", error);
    }
  };

  const handleVote = async () => {
    if (!selectedCandidate) {
      alert("‚ö†Ô∏è Please select a candidate first!");
      return;
    }

    try {
      const { _id, name } = selectedCandidate;
      await axios.post("http://localhost:5000/api/votes/vote", {
        voterId,
        candidateId: _id,
        candidateName: name,
      });

      alert("üéâ Vote cast successfully!");
      setHasVoted(true); // ‚úÖ Set hasVoted state after voting
    } catch (error) {
      console.error("‚ùå Error voting:", error.response?.data || error.message);
      alert("Error casting vote.");
    }
  };

  return (
    <div className="voter-dashboard">
      <h2>üó≥ Voter Dashboard</h2>

      {hasVoted ? (
        <h3>‚úÖ Thanks for Voting!</h3> // ‚úÖ Show "Thanks for Voting" message
      ) : isVerified ? (
        <div>
          <h3>Select a Candidate:</h3>
          {candidates.length === 0 ? (
            <p>Loading candidates...</p>
          ) : (
            <div className="candidates-container">
              {candidates.map((candidate) => (
                <div
                  key={candidate._id}
                  className={`candidate-card ${selectedCandidate?._id === candidate._id ? "selected" : ""}`}
                  onClick={() => setSelectedCandidate(candidate)}
                >
                  <h3>{candidate.name} ({candidate.party})</h3>
                  <img src={`http://localhost:5000/uploads/${candidate.logo}`} alt={candidate.name} />
                </div>
              ))}
            </div>
          )}
          <button onClick={handleVote} disabled={!selectedCandidate}>Vote</button>
        </div>
      ) : (
        <p>Verifying Voter ID...</p>
      )}
    </div>
  );
};

export default VoterDashboard;
