import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CandidateManagement.css';

const CandidateManagement = () => {
  const [candidates, setCandidates] = useState([]);
  const [candidateName, setCandidateName] = useState('');
  const [party, setParty] = useState('');
  const [image, setImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  
  const [editCandidateId, setEditCandidateId] = useState(null);
  const [editCandidateName, setEditCandidateName] = useState('');
  const [editParty, setEditParty] = useState('');
  const [editImage, setEditImage] = useState(null);
  const [editPreviewImage, setEditPreviewImage] = useState(null);

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/api/candidates');
      setCandidates(response.data);
    } catch (error) {
      setMessage('Error fetching candidates.');
    } finally {
      setLoading(false);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    setPreviewImage(URL.createObjectURL(file));
  };

  const handleAddCandidate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', candidateName);
    formData.append('party', party);
    if (image) formData.append('image', image);
    
    try {
      await axios.post('http://localhost:5000/api/candidates/add', formData);
      setMessage('Candidate added successfully!');
      fetchCandidates();
      setCandidateName('');
      setParty('');
      setImage(null);
      setPreviewImage(null);
    } catch (error) {
      setMessage('Error adding candidate.');
    }
  };

  const handleDeleteCandidate = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/candidates/delete/${id}`);
      setMessage('Candidate deleted successfully!');
      fetchCandidates();
    } catch (error) {
      setMessage('Error deleting candidate.');
    }
  };

  const handleEditClick = (candidate) => {
    setEditCandidateId(candidate._id);
    setEditCandidateName(candidate.name);
    setEditParty(candidate.party);
    setEditPreviewImage(`http://localhost:5000/uploads/${candidate.image_filename}`);
  };

  const handleCancelEdit = () => {
    setEditCandidateId(null);
    setEditCandidateName('');
    setEditParty('');
    setEditImage(null);
    setEditPreviewImage(null);
  };

  const handleUpdateCandidate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', editCandidateName);
    formData.append('party', editParty);
    if (editImage) formData.append('image', editImage);

    try {
      await axios.put(`http://localhost:5000/api/candidates/update/${editCandidateId}`, formData);
      setMessage('Candidate updated successfully!');
      fetchCandidates();
      handleCancelEdit();
    } catch (error) {
      setMessage('Error updating candidate.');
    }
  };

  return (
    <div className="candidate-management">
      <h2>Candidate Management</h2>
      {message && <div className="message">{message}</div>}

      <form onSubmit={handleAddCandidate}>
        <input type="text" placeholder="Candidate Name" value={candidateName} onChange={(e) => setCandidateName(e.target.value)} required />
        <input type="text" placeholder="Party Name" value={party} onChange={(e) => setParty(e.target.value)} required />
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {previewImage && <img src={previewImage} alt="Preview" className="image-preview" />}
        <button type="submit" disabled={!candidateName || !party}>Add Candidate</button>
      </form>

      <h3>Candidate List</h3>
      {loading ? <p>Loading candidates...</p> : (
        <div className="candidate-list">
          {candidates.map((candidate) => (
            <div key={candidate._id} className="candidate-card">
              <img src={`http://localhost:5000/uploads/${candidate.image_filename}`} alt="Candidate" className="candidate-image" />
              <h4>{candidate.name}</h4>
              <p>{candidate.party}</p>
              <button onClick={() => handleDeleteCandidate(candidate._id)}>Delete</button>
              <button onClick={() => handleEditClick(candidate)}>Update</button>
            </div>
          ))}
        </div>
      )}

      {editCandidateId && (
        <div className="update-form">
          <h3>Update Candidate</h3>
          <form onSubmit={handleUpdateCandidate}>
            <input type="text" value={editCandidateName} onChange={(e) => setEditCandidateName(e.target.value)} required />
            <input type="text" value={editParty} onChange={(e) => setEditParty(e.target.value)} required />
            <input type="file" accept="image/*" onChange={(e) => { setEditImage(e.target.files[0]); setEditPreviewImage(URL.createObjectURL(e.target.files[0])); }} />
            {editPreviewImage && <img src={editPreviewImage} alt="Preview" className="image-preview" />}
            <button type="submit">Save</button>
            <button type="button" onClick={handleCancelEdit}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default CandidateManagement;
