import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [selectedCandidate, setSelectedCandidate] = useState(null);
  const [isLoading, setIsLoading] = useState(true); // ‚úÖ Added loading state
  const voterId = localStorage.getItem("voter_id"); // ‚úÖ Get voter ID from localStorage

  // ‚úÖ Verify if the voter has voted
  const verifyVoter = async () => {
    if (!voterId) {
      console.error("No voter ID found.");
      setIsLoading(false);
      return;
    }

    try {
      const response = await axios.get(`http://localhost:5000/api/voter/check-vote/${voterId}`);

      if (response.data.success) {
        if (response.data.message.includes("already voted")) {
          setHasVoted(true); // ‚úÖ Directly show "Thanks for Voting!"
        } else {
          setIsVerified(true);
          fetchCandidates(); // ‚úÖ Fetch candidates only if voter hasn't voted
        }
      } else {
        console.error("‚ùå Invalid Voter ID.");
      }
    } catch (error) {
      console.error("‚ùå Error verifying voter:", error);
    } finally {
      setIsLoading(false); // ‚úÖ Stop loading after verification
    }
  };

  // ‚úÖ Fetch candidates after verifying voter
  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(response.data);
    } catch (error) {
      console.error("‚ùå Error fetching candidates:", error);
    }
  };

  useEffect(() => {
    verifyVoter(); // ‚úÖ Call once when component mounts
  }, []);

  // ‚úÖ Handle vote submission
  const handleVote = async () => {
    if (!selectedCandidate) {
      alert("‚ö†Ô∏è Please select a candidate first!");
      return;
    }

    try {
      const { _id, name } = selectedCandidate;
      const response = await axios.post("http://localhost:5000/api/voter/vote", {
        voterId,
        candidateId: _id,
        candidateName: name,
      });

      if (response.data.success) {
        alert("üéâ Vote cast successfully!");
        setHasVoted(true); // ‚úÖ Mark as voted
      } else if (response.data.message === "You have already voted!") {
        setHasVoted(true); // ‚úÖ Fix: Update UI instead of showing alert
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      console.error("‚ùå Error voting:", error.response?.data || error.message);

      if (error.response?.data?.message === "You have already voted!") {
        setHasVoted(true); // ‚úÖ Show "Thanks for Voting"
      } else {
        alert("Error casting vote.");
      }
    }
  };

  return (
    <div className="voter-dashboard">
      <h2>üó≥ Voter Dashboard</h2>

      {isLoading ? (
        <p>‚è≥ Verifying Voter ID...</p>
      ) : hasVoted ? (
        <h3>‚úÖ Thanks for Voting!</h3>
      ) : isVerified ? (
        <div>
          <h3>Select a Candidate:</h3>
          {candidates.length === 0 ? (
            <p>Loading candidates...</p>
          ) : (
            <div className="candidates-container">
              {candidates.map((candidate) => (
                <div
                  key={candidate._id}
                  className={`candidate-card ${selectedCandidate?._id === candidate._id ? "selected" : ""}`}
                  onClick={() => setSelectedCandidate(candidate)}
                >
                  <h3>{candidate.name} ({candidate.party})</h3>
                  <img src={`http://localhost:5000/uploads/${candidate.logo}`} alt={candidate.name} />
                </div>
              ))}
            </div>
          )}
          <button onClick={handleVote} disabled={!selectedCandidate}>Vote</button>
        </div>
      ) : (
        <p>‚ùå Verification Failed. Please try again.</p>
      )}
    </div>
  );
};

export default VoterDashboard;
