import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  const voterId = localStorage.getItem("voter_id");

  useEffect(() => {
    const verifyVoter = async () => {
      if (!voterId) {
        console.error("‚ùå No voter ID found.");
        setIsLoading(false);
        return;
      }

      try {
        const response = await axios.get(
          `http://localhost:5000/api/voter/check-vote/${voterId}`
        );

        if (response.data.success) {
          if (response.data.hasVoted) {
            setHasVoted(true);
          } else {
            setIsVerified(true);
            fetchCandidates();
          }
        } else {
          if (response.data.message.includes("already voted")) {
            setHasVoted(true);
          }
        }
      } catch (error) {
        if (error.response?.data?.message?.includes("already voted")) {
          setHasVoted(true);
        }
      } finally {
        setIsLoading(false);
      }
    };

    verifyVoter();
  }, [voterId]);

  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(response.data);
    } catch (error) {
      console.error("‚ùå Error fetching candidates:", error);
    }
  };

  const handleVote = async (candidate) => {
    if (!candidate) {
      alert("‚ö†Ô∏è Please select a candidate first!");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/voter/vote", {
        voterId,
        candidateId: candidate._id,
      });

      if (response.data.success) {
        alert("üéâ Vote cast successfully!");
        setHasVoted(true);
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      if (error.response?.data?.message?.includes("already voted")) {
        setHasVoted(true);
      }
    }
  };

  return (
    <div className="voter-dashboard">
      <h2>üó≥ Online Voting System</h2>

      {isLoading ? (
        <p>‚è≥ Verifying Voter ID...</p>
      ) : hasVoted ? (
        <h3>‚úÖ Thanks for Voting!</h3>
      ) : isVerified ? (
        <div className="candidates-container">
          {candidates.length === 0 ? (
            <p>‚è≥ Loading candidates...</p>
          ) : (
            candidates.map((candidate) => (
              <div className="candidate-row" key={candidate._id}>
                <img
                  src={`http://localhost:5000/uploads/${candidate.logo}`}
                  alt="Candidate Logo"
                  className="candidate-logo"
                />
                <button onClick={() => handleVote(candidate)} className="vote-button">
                  Vote
                </button>
              </div>
            ))
          )}
        </div>
      ) : (
        <p>‚ùå Verification Failed. Please try again.</p>
      )}
    </div>
  );
};

export default VoterDashboard;
