import { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";

const FaceRecognition = () => {
    const videoRef = useRef(null);
    const navigate = useNavigate();
    const [message, setMessage] = useState("");

    useEffect(() => {
        navigator.mediaDevices.getUserMedia({ video: true })
            .then((stream) => {
                if (videoRef.current) videoRef.current.srcObject = stream;
            })
            .catch((error) => console.error("Camera access denied:", error));
    }, []);

    const verifyFace = async () => {
        const voterId = localStorage.getItem("voterId");

        const response = await fetch("http://localhost:5000/api/auth/verify-face", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ voterId }),
        });

        const data = await response.json();
        if (data.success) {
            setMessage("Face matched! Proceeding to voting...");
            setTimeout(() => navigate("/vote"), 2000);
        } else {
            setMessage("Face not recognized. Try again.");
        }
    };

    return (
        <div>
            <h2>Face Recognition</h2>
            <video ref={videoRef} autoPlay></video>
            <button onClick={verifyFace}>Verify Face</button>
            <p>{message}</p>
        </div>
    );
};

export default FaceRecognition;
