import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css"; // ✅ Import CSS file

const API_URL = "http://localhost:5000/api"; // ✅ Adjust API URL

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]); // ✅ Store fetched candidates
  const [votedCandidate, setVotedCandidate] = useState(null);
  const [loading, setLoading] = useState(true); // ✅ Show loading state
  const [error, setError] = useState(""); // ✅ Store API errors
  const [voteSuccess, setVoteSuccess] = useState(""); // ✅ Store vote success message
  const [isVoting, setIsVoting] = useState(null); // ✅ Track which button is clicked

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await axios.get(`${API_URL}/candidates`);
      setCandidates(res.data || []);
    } catch (error) {
      console.error("Error fetching candidates:", error);
      setError("❌ Failed to load candidates. Please refresh.");
    } finally {
      setLoading(false);
    }
  };

  const handleVote = async (candidateId, candidateName) => {
    if (votedCandidate) return; // ✅ Prevent multiple votes
    setIsVoting(candidateId); // ✅ Disable only the clicked button

    try {
        setVoteSuccess("");
        setError("");

        // ✅ Step 1: Cast vote in MongoDB
        const voteResponse = await axios.post(`${API_URL}/vote`, { candidateName });
        if (!voteResponse.data.success) throw new Error("Vote recording failed.");

        // ✅ Step 2: Update vote count in Google Sheets
        const sheetResponse = await axios.post(`${API_URL}/updateGoogleSheets`, { candidateName });
        if (!sheetResponse.data.success) throw new Error("Google Sheets update failed.");

        // ✅ Step 3: Show thank you message and close after 3 seconds
        setVoteSuccess(`✅ Thank you for voting!`);
        setTimeout(() => {
            window.close(); // ✅ Closes the voting window/tab
        }, 3000);

    } catch (error) {
        console.error("Error voting:", error);
        setError(error.message || "❌ Failed to cast vote. Try again.");
    } finally {
        setIsVoting(null);
    }
};


  return (
    <div className="voter-container">
      

      {loading ? (
        <p className="loading-message">⏳ Loading candidates...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : (
        <>
          {voteSuccess && <p className="success-message">{voteSuccess}</p>}
          <div className="candidate-list">
          <h1 className="title_1">Online Voting System</h1>
            {candidates.length > 0 ? (
              candidates.map((candidate) => (
                <div key={candidate._id} className="candidate-row">
                  {/* Left: Party Logo & Name */}
                  <div className="candidate-info">
                    <img
                      src={`http://localhost:5000/uploads/${candidate.logo}`}
                      alt={candidate.name}
                      className="candidate-logo"
                    />
                    <p className="candidate-name">{candidate.name}</p>
                    <p className="vote-count">Votes: {candidate.votes || 0}</p>
                  </div>

                  {/* Right: Vote Button */}
                  <button
                    className={`vote-button ${votedCandidate ? "disabled" : ""}`}
                    onClick={() => handleVote(candidate._id, candidate.name)}
                    disabled={votedCandidate !== null || isVoting !== null}
                  >
                    {isVoting === candidate._id
                      ? "Voting..."
                      : votedCandidate === candidate._id
                      ? "Voted"
                      : "Vote"}
                  </button>
                </div>
              ))
            ) : (
              <p className="no-candidates">⚠️ No candidates available.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default VoterDashboard;
