import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

const Login = () => {
    const [id, setId] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize useNavigate

    const handleLogin = async (e) => {
        e.preventDefault();
        
        // Check if the ID is for admin or voter
        if (id === '8143796138') {
            // Redirect to admin dashboard
            navigate('/admin-dashboard');
        } else {
            // Simulate a voter login (you can replace this with an actual API call)
            const isVoterValid = await checkVoterId(id); // Replace with your actual voter ID validation logic
            if (isVoterValid) {
                // Redirect to voter dashboard
                navigate('/voter-dashboard');
            } else {
                setError('Invalid voter ID. Please try again.');
            }
        }
    };

    // Simulated function to check if the voter ID is valid
    const checkVoterId = async (voterId) => {
        // Replace this with an actual API call to validate the voter ID
        const response = await fetch(`http://localhost:5000/api/voters/${voterId}`);
        const data = await response.json();
        return data.exists; // Assuming the API returns an object with an 'exists' property
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">Login</h2>
            <form onSubmit={handleLogin}>
                <div className="form-group">
                    <label htmlFor="adminId">ID</label>
                    <input
                        type="text"
                        className="form-control"
                        id="adminId"
                        value={id}
                        onChange={(e) => setId(e.target.value)}
                        required
                    />
                </div>
                {error && <div className="alert alert-danger">{error}</div>}
                <button type="submit" className="btn btn-primary btn-block">
                    Login
                </button>
            </form>
        </div>
    );
};

export default Login;
