import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:5000/api";
const voter_id = "456789123025"; // Hardcoded voter ID

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [hasVoted, setHasVoted] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const candidatesRes = await axios.get(`${API_URL}/candidates, { voter_id }`);
        const voterStatusRes = await axios.post(`${API_URL}/voter/check-voter`, { voter_id });

        setCandidates(candidatesRes.data.candidates || []);
        if (!voterStatusRes.data.success) setHasVoted(true);
      } catch (error) {
        console.error("‚ùå Error:", error);
        setError("Failed to load data.");
      }
    };

    fetchData();
  }, []);

  const handleVote = async (candidateId) => {
    if (hasVoted) return;

    try {
      const response = await axios.post(`${API_URL}/candidates/vote`, { voter_id, candidate_id: candidateId });

      if (response.data.success) {
        setHasVoted(true);
        alert("Vote recorded successfully!");
      }
    } catch (error) {
      console.error("‚ùå Error voting:", error);
      setError("Error submitting vote.");
    }
  };

  return (
    <div>
      <h2>üó≥ Voter Dashboard</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {hasVoted ? <p>‚úÖ You have already voted.</p> : (
        candidates.map((candidate) => (
          <div key={candidate._id}>
            <p>{candidate.name} ({candidate.party})</p>
            <button onClick={() => handleVote(candidate._id)}>Vote</button>
          </div>
        ))
      )}
    </div>
  );
};

export default VoterDashboard;
