import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css"; // ‚úÖ Import CSS file

const API_URL = "http://localhost:5000/api"; // ‚úÖ Adjust API URL

const VoterDashboard = ({ voter_id }) => {
  const [candidates, setCandidates] = useState([]); // ‚úÖ Store fetched candidates
  const [hasVoted, setHasVoted] = useState(false); // ‚úÖ Check if voter has voted
  const [isVoting, setIsVoting] = useState(false); // ‚úÖ Prevent multiple clicks

  useEffect(() => {
    if (voter_id) {
      checkVoterStatus();
      fetchCandidates();
    }
  }, [voter_id]);

  /** ‚úÖ Check if voter has already voted */
  const checkVoterStatus = async () => {
    try {
      const res = await axios.post(`${API_URL}/check-voter`, { voter_id });

      if (res.data.success === false) {
        setHasVoted(true); // ‚úÖ Disable voting
      }
    } catch (error) {
      console.error("Error checking voter status:", error);
    }
  };

  /** ‚úÖ Fetch Candidates */
  const fetchCandidates = async () => {
    try {
      const res = await axios.get(`${API_URL}/candidates`);
      setCandidates(res.data || []);
    } catch (error) {
      console.error("Error fetching candidates:", error);
      setCandidates([]);
    }
  };

  /** ‚úÖ Handle Voting */
  const handleVote = async (candidateId, candidateName) => {
    if (isVoting || hasVoted) return; // ‚úÖ Prevent multiple votes
    setIsVoting(true);

    try {
      // 1Ô∏è‚É£ ‚úÖ Cast vote in backend (MongoDB)
      await axios.post(`${API_URL}/vote`, { voter_id, candidateName });

      // 2Ô∏è‚É£ ‚úÖ Update vote count in Google Sheets (Handled in backend)
      await axios.post(`${API_URL}/updateGoogleSheets`, { candidateName });

      // 3Ô∏è‚É£ ‚úÖ Update UI
      setCandidates((prevCandidates) =>
        prevCandidates.map((c) =>
          c._id === candidateId ? { ...c, votes: (c.votes || 0) + 1 } : c
        )
      );

      // 4Ô∏è‚É£ ‚úÖ Mark voter as voted
      setHasVoted(true);
      alert(`‚úÖ Your vote for ${candidateName} has been recorded successfully!`);
    } catch (error) {
      console.error("Error voting:", error);
      alert("‚ùå Failed to cast vote. Try again.");
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">üó≥Ô∏è Online Voting System</h1>

      {hasVoted ? (
        <p className="already-voted">‚ö†Ô∏è You have already voted. Voting is not allowed again.</p>
      ) : (
        <div className="candidate-list">
          {candidates.length > 0 ? (
            candidates.map((candidate) => (
              <div key={candidate._id} className="candidate-row">
                <div className="candidate-info">
                  <img
                    src={`http://localhost:5000/uploads/${candidate.logo}`}
                    alt={candidate.name}
                    className="candidate-logo"
                  />
                  <p className="candidate-name">{candidate.name}</p>
                  <p className="vote-count">Votes: {candidate.votes || 0}</p>
                </div>

                {/* Disable voting if voter has already voted */}
                <button
                  className={`vote-button ${hasVoted ? "disabled" : ""}`}
                  onClick={() => handleVote(candidate._id, candidate.name)}
                  disabled={hasVoted || isVoting}
                >
                  {isVoting ? "Voting..." : "üó≥Ô∏è Vote"}
                </button>
              </div>
            ))
          ) : (
            <p className="no-candidates">‚ö†Ô∏è No candidates available.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default VoterDashboard;
