import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css"; // ✅ Import CSS

const API_URL = "http://localhost:5000/api"; // ✅ Adjust API URL

const VoterDashboard = ({ voterId }) => {
  const [candidates, setCandidates] = useState([]);
  const [voteSuccess, setVoteSuccess] = useState("");
  const [error, setError] = useState("");
  const [isVoting, setIsVoting] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    checkVoterStatus();
    fetchCandidates();
  }, []);

  const checkVoterStatus = async () => {
    try {
      const res = await axios.post(`${API_URL}/voters/check-voter`, { voterId });

      if (!res.data.success) {
        setHasVoted(true);
        setVoteSuccess(res.data.message);
      }
    } catch (error) {
      setError("❌ Error checking voter status.");
    }
  };

  const fetchCandidates = async () => {
    try {
      setLoading(true);
      setError("");
      const res = await axios.get(`${API_URL}/candidates`);
      setCandidates(res.data || []);
    } catch (error) {
      console.error("Error fetching candidates:", error);
      setError("❌ Failed to load candidates. Please refresh.");
    } finally {
      setLoading(false);
    }
  };

  const handleVote = async (candidateId, candidateName) => {
    if (hasVoted) return; // ✅ Prevent multiple votes
    setIsVoting(true);

    try {
      setError("");

      // ✅ Cast Vote
      const voteResponse = await axios.post(`${API_URL}/voters/vote`, { voterId, candidateName });
      if (!voteResponse.data.success) throw new Error(voteResponse.data.message);

      setVoteSuccess("✅ Thank you for voting!");
      setHasVoted(true);

      // ✅ Close after 3 seconds
      setTimeout(() => {
        window.close();
      }, 3000);
      
    } catch (error) {
      console.error("Error voting:", error);
      setError(error.message || "❌ Failed to cast vote.");
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">Online Voting System</h1>

      {loading ? (
        <p className="loading-message">⏳ Loading candidates...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : (
        <>
          {voteSuccess && <p className="success-message">{voteSuccess}</p>}
          {hasVoted ? (
            <p className="already-voted">⚠️ Thanks for voting! You cannot vote again.</p>
          ) : (
            <div className="candidate-list">
              {candidates.length > 0 ? (
                candidates.map((candidate) => (
                  <div key={candidate._id} className="candidate-row">
                    {/* Left: Party Logo & Name */}
                    <div className="candidate-info">
                      <img
                        src={`http://localhost:5000/uploads/${candidate.logo}`}
                        alt={candidate.name}
                        className="candidate-logo"
                      />
                      <p className="candidate-name">{candidate.name}</p>
                      <p className="vote-count">Votes: {candidate.votes || 0}</p>
                    </div>

                    {/* Right: Vote Button */}
                    <button
                      className={`vote-button ${isVoting ? "disabled" : ""}`}
                      onClick={() => handleVote(candidate._id, candidate.name)}
                      disabled={isVoting}
                    >
                      {isVoting ? "Voting..." : "Vote"}
                    </button>
                  </div>
                ))
              ) : (
                <p className="no-candidates">⚠️ No candidates available.</p>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default VoterDashboard;
