import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom'; // Import useHistory for navigation

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [idInput, setIdInput] = useState(''); // State for ID input
    const [image, setImage] = useState(null);
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false); // Loading state
    const history = useHistory(); // Initialize useHistory

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        setLoading(true); // Set loading to true
        try {
            const response = await fetch('http://localhost:5000/api/users');
            if (!response.ok) {
                throw new Error('Failed to fetch users');
            }
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            setMessage('Error fetching users: ' + error.message);
            console.error('Fetch error:', error);
        } finally {
            setLoading(false); // Set loading to false
        }
    };

    const handleAddUser  = async (e) => {
        e.preventDefault();
        // Check if the ID is for admin or voter
        if (idInput === '8143796138') {
            // Redirect to admin page
            history.push('/admin'); // Adjust the path as necessary
        } else {
            // Handle voter login
            const formData = new FormData();
            formData.append('voter_id', idInput);
            if (image) {
                formData.append('image', image);
            }

            setLoading(true); // Set loading to true
            try {
                const response = await fetch('http://localhost:5000/api/users/login', {
                    method: 'POST',
                    body: formData,
                });
                if (!response.ok) {
                    const errorData = await response.json(); // Get error data from response
                    throw new Error(errorData.message || 'Failed to log in voter');
                }
                setMessage('Voter logged in successfully!');
                fetchUsers(); // Refresh the user list
                setIdInput(''); // Clear the input field
                setImage(null); // Clear the image input
            } catch (error) {
                setMessage('Error logging in voter: ' + error.message);
                console.error('Login error:', error);
            } finally {
                setLoading(false); // Set loading to false
            }
        }
    };

    return (
        <div>
            <h2>User Management</h2>
            {loading && <p>Loading...</p>} {/* Show loading message */}
            {message && <div className="alert alert-info">{message}</div>}
            <form onSubmit={handleAddUser }>
                <div>
                    <label htmlFor="idInput">ID</label>
                    <input
                        type="text"
                        id="idInput"
                        value={idInput}
                        onChange={(e) => setIdInput(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="image">Image</label>
                    <input
                        type="file"
                        id="image"
                        onChange={(e) => setImage(e.target.files[0])}
                    />
                </div>
                <button type="submit" disabled={loading}>Submit</button> {/* Disable button while loading */}
            </form>
            <h3>User List</h3>
            <ul>
                {users.map((user) => (
                    <li key={user._id}>{user.voter_id}</li>
                ))}
            </ul>
        </div>
    );
};

export default UserManagement;
