import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [selectedCandidate, setSelectedCandidate] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const voterId = localStorage.getItem("voter_id"); // ✅ Get voter ID

  useEffect(() => {
    const verifyVoter = async () => {
      if (!voterId) {
        console.error("❌ No voter ID found in localStorage.");
        setIsLoading(false);
        return;
      }

      try {
        console.log(`🔍 Checking voter ID: ${voterId}`);
        const response = await axios.get(`http://localhost:5000/api/voter/check-vote/${voterId}`);

        console.log("✅ API Response:", response.data);

        if (response.data.success) {
          if (response.data.hasVoted) {
            setHasVoted(true); // ✅ Update UI if voter has already voted
          } else {
            setIsVerified(true);
            fetchCandidates(); // ✅ Fetch candidates only if voter hasn't voted
          }
        } else {
          console.error("❌ Invalid Voter ID:", response.data.message);
        }
      } catch (error) {
        console.error("❌ Error verifying voter:", error.response?.data || error.message);
      } finally {
        setIsLoading(false);
      }
    };

    verifyVoter(); // ✅ Call verification function inside `useEffect`
  }, [voterId]); // ✅ Add `voterId` as a dependency

  // ✅ Fetch Candidates (Only if verified)
  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(response.data);
    } catch (error) {
      console.error("❌ Error fetching candidates:", error);
    }
  };

  // ✅ Handle Vote Submission
  const handleVote = async () => {
    if (!selectedCandidate) {
      alert("⚠️ Please select a candidate first!");
      return;
    }

    try {
      const { _id, name } = selectedCandidate;
      console.log(`🗳 Voting for Candidate: ${name} (ID: ${_id})`);

      const response = await axios.post("http://localhost:5000/api/voter/vote", {
        voterId,
        candidateId: _id,
        candidateName: name,
      });

      console.log("📨 Vote Response:", response.data);

      if (response.data.success) {
        alert("🎉 Vote cast successfully!");
        setHasVoted(true); // ✅ Immediately update UI
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      console.error("❌ Error voting:", error.response?.data || error.message);
      alert("⚠️ Error casting vote. Please try again.");
    }
  };

  return (
    <div className="voter-dashboard">
      <h2>🗳 Voter Dashboard</h2>

      {isLoading ? (
        <p>⏳ Verifying Voter ID...</p>
      ) : hasVoted ? (
        <h3>✅ Thanks for Voting!</h3>
      ) : isVerified ? (
        <div>
          <h3>Select a Candidate:</h3>
          {candidates.length === 0 ? (
            <p>⏳ Loading candidates...</p>
          ) : (
            <div className="candidates-container">
              {candidates.map((candidate) => (
                <div
                  key={candidate._id}
                  className={`candidate-card ${selectedCandidate?._id === candidate._id ? "selected" : ""}`}
                  onClick={() => setSelectedCandidate(candidate)}
                >
                  <h3>{candidate.name} ({candidate.party})</h3>
                  <img src={`http://localhost:5000/uploads/${candidate.logo}`} alt={candidate.name} />
                </div>
              ))}
            </div>
          )}
          <button onClick={handleVote} disabled={!selectedCandidate}>Vote</button>
        </div>
      ) : (
        <p>❌ Verification Failed. Please try again.</p>
      )}
    </div>
  );
};

export default VoterDashboard;
