import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(localStorage.getItem("hasVoted") === "true");
  const [isLoading, setIsLoading] = useState(true);

  const voterId = localStorage.getItem("voter_id");

  useEffect(() => {
    const verifyVoter = async () => {
      if (!voterId) {
        console.error("‚ùå No voter ID found.");
        setIsLoading(false);
        return;
      }
  
      try {
        console.log(`üîç Checking voter ID: ${voterId}`);
        const response = await axios.get(
          `http://localhost:5000/api/voter/check-vote/${voterId}`
        );
  
        console.log("‚úÖ API Response:", response.data);
  
        if (response.data.success) {
          if (response.data.hasVoted) {
            console.log("üéâ Voter has already voted. Showing 'Thanks for Voting!'");
            setHasVoted(true);
            localStorage.setItem("hasVoted", "true");
            setIsVerified(false); // Prevent voting again
          } else {
            setHasVoted(false);
            localStorage.removeItem("hasVoted"); // Reset if they haven't voted
            setIsVerified(true); // Allow voting
            fetchCandidates();
          }
        } else if (response.data.message === "Voter has already voted") {
          console.log("üéâ Voter has already voted. Showing 'Thanks for Voting!'");
          setHasVoted(true);
          localStorage.setItem("hasVoted", "true");
          setIsVerified(false);
        } else {
          console.error("‚ùå Voter verification failed:", response.data.message);
          setIsVerified(false);
        }
      } catch (error) {
        console.error("‚ùå Error verifying voter:", error.response?.data || error.message);
        setIsVerified(false);
      } finally {
        setIsLoading(false);
      }
    };
  
    verifyVoter();
  }, [voterId]);
  

  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(response.data);
    } catch (error) {
      console.error("‚ùå Error fetching candidates:", error);
    }
  };

  const handleVote = async (candidate) => {
    if (!candidate) {
      alert("‚ö†Ô∏è Please select a candidate first!");
      return;
    }
  
    try {
      console.log(`üó≥ Voting for Candidate: ${candidate.name} (ID: ${candidate._id})`);
  
      const response = await axios.post("http://localhost:5000/api/voter/vote", {
        voterId,
        candidateId: candidate._id,
        candidateName: candidate.name,
      });
  
      console.log("üì® Vote Response:", response.data);
  
      if (response.data.success) {
        // ‚úÖ Open a new window with a thank-you message BEFORE updating the state
        const newWindow = window.open("", "_blank", "width=400,height=300");
        newWindow.document.write(`
          <html>
            <head>
              <title>Vote Successful</title>
              <style>
                body { font-family: Arial, sans-serif; text-align: center; padding: 20px; }
                h2 { color: green; }
                button { padding: 10px 15px; font-size: 16px; cursor: pointer; margin-top: 20px; }
              </style>
            </head>
            <body>
              <h2>üéâ Thanks for Voting!</h2>
              <p>Your vote has been successfully cast.</p>
              <button onclick="window.close()">Close</button>
            </body>
          </html>
        `);
  
        // ‚úÖ Wait for the user to see the message, then update localStorage & UI
        setTimeout(() => {
          localStorage.setItem("hasVoted", "true"); // Persist voting status
          setHasVoted(true);
        }, 1000); // Small delay for better UX
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      console.error("‚ùå Error voting:", error.response?.data || error.message);
    }
  };
  
  return (
    <div className="voter-dashboard">
      <h2>üó≥ Online Voting System</h2>

      {isLoading ? (
  <p>‚è≥ Verifying Voter ID...</p>
) : hasVoted ? (
  <h3>‚úÖ Thanks for Voting!</h3>  // ‚úÖ This will show IMMEDIATELY after clicking "Vote"
) : isVerified ? (
  <div>
    <h3>Select a Candidate:</h3>
    {candidates.length === 0 ? (
      <p>‚è≥ Loading candidates...</p>
    ) : (
      <div className="candidates-container">
        {candidates.map((candidate) => (
          <div className="candidate-row" key={candidate._id}>
            <img
              src={`http://localhost:5000/uploads/${candidate.logo}`}
              alt={candidate.name}
              className="candidate-logo"
            />
            <div className="candidate-info">
              <h3>{candidate.name}</h3>
              <p>Party: {candidate.party}</p>
            </div>
            <button onClick={() => handleVote(candidate)} className="vote-button" disabled={hasVoted}>
              {hasVoted ? "Voted ‚úÖ" : "Vote"}
            </button>
          </div>
        ))}
      </div>
    )}
  </div>
) : (
  <h3>Thanks for Voting</h3>
)}

    </div>
  );
  
  
};

export default VoterDashboard;
