import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Candidate.css";

const CandidateManagement = () => {
    const [candidates, setCandidates] = useState([]);
    const [name, setName] = useState("");
    const [party, setParty] = useState("");
    const [logo, setLogo] = useState(null);
    const [editingId, setEditingId] = useState(null);
    const [message, setMessage] = useState("");

    // Fetch all candidates
    useEffect(() => {
        fetchCandidates();
      }, []);
    
      // Function to fetch all users
      const fetchCandidates = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/candidates');
          setCandidates(response.data);
        } catch (error) {
          console.error('Error fetching users:', error);
          setMessage('Error fetching users.');
        }
      };

    // Handle file selection
    const handleFileChange = (event) => {
        setLogo(event.target.files[0]);
    };

    // Add or update candidate
    const handleSubmit = async (event) => {
        event.preventDefault();

        if (!name || !party) {
            alert("Please fill all fields.");
            return;
        }

        const formData = new FormData();
        formData.append("name", name);
        formData.append("party", party);
        if (logo) formData.append("logo", logo);

        try {
            if (editingId) {
                await axios.put(`http://localhost:5000/api/candidates/update/${editingId}`, formData, {
                    headers: { "Content-Type": "multipart/form-data" },
                });
                setMessage("Candidate updated successfully!");
            } else {
                await axios.post("http://localhost:5000/api/candidates/addCandidate", formData, {
                    headers: { "Content-Type": "multipart/form-data" },
                });
                setMessage("Candidate added successfully!");
            }

            fetchCandidates(); // Refresh the list
            setName("");
            setParty("");
            setLogo(null);
            setEditingId(null);

            // Clear message after 3 seconds
            setTimeout(() => setMessage(""), 3000);
        } catch (error) {
            console.error("Error saving candidate:", error.response?.data || error.message);
        }
    };

    // Delete candidate
    const handleDeleteCandidate = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/candidates/delete/${id}`);
            fetchCandidates();
            setMessage("Candidate deleted successfully!");

            // Clear message after 3 seconds
            setTimeout(() => setMessage(""), 3000);
        } catch (error) {
            console.error("Error deleting candidate:", error.response?.data || error.message);
        }
    };

    // Load candidate data for editing
    const handleEditCandidate = (candidate) => {
        setName(candidate.name);
        setParty(candidate.party);
        setEditingId(candidate._id);
    };

    return (
        <div className="candidate-management">
            <h2 className="title">Candidate Management</h2>

            {/* Success Message */}
            {message && <div className="message">{message}</div>}

            {/* Form to Add/Update Candidate */}
            <form onSubmit={handleSubmit} className="candidate-form">
                <input
                    type="text"
                    placeholder="Candidate Name"
                    value={name}
                    className="input-field"
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Party Name"
                    value={party}
                    className="input-field"
                    onChange={(e) => setParty(e.target.value)}
                    required
                />
                <input type="file" accept="image/*" onChange={handleFileChange} className="file-input" />
                <button type="submit" className="add-button">
                    {editingId ? "Update Candidate" : "Add Candidate"}
                </button>
            </form>

            {/* Candidate List */}
            {/* <h3 className="subtitle">Candidate List</h3>
            <ul className="candidate-list">
                {candidates.map((candidate) => (
                    <li key={candidate._id} className="candidate-item">
                        {candidate.logo && (
                            <img
                                src={`http://localhost:5000/uploads/${candidate.logo}`}
                                alt={candidate.name}
                                className="candidate-logo"
                                onError={(e) => (e.target.style.display = "none")}
                            />
                        )}
                        <span className="candidate-info">{candidate.name} - {candidate.party}</span>
                        <div className="button-group">
                            <button onClick={() => handleEditCandidate(candidate)} className="edit-button">
                                Edit
                            </button>
                            <button onClick={() => handleDeleteCandidate(candidate._id)} className="delete-button">
                                Delete
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}; */}
