import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [voterId, setVoterId] = useState('');
    const [error, setError] = useState('');
    const videoRef = useRef(null);
    const navigate = useNavigate();

    // ✅ Start Camera
    const startCamera = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ video: true });
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
            }
        } catch (error) {
            console.error("Error accessing camera:", error);
        }
    };

    // ✅ Capture Image
    const captureImage = () => {
        const canvas = document.createElement('canvas');
        const video = videoRef.current;
        if (!video) return null;

        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);
        
        return canvas.toDataURL('image/jpeg'); // Convert to Base64
    };

    // ✅ Handle Voter Login
    const handleLogin = async () => {
        if (!voterId) {
            setError('Please enter your Voter ID.');
            return;
        }

        // Capture image from webcam
        const imageData = captureImage();
        if (!imageData) {
            setError('Face capture failed. Try again.');
            return;
        }

        const formData = new FormData();
        formData.append('voter_id', voterId);
        formData.append('image', dataURLtoBlob(imageData));

        try {
            const response = await fetch('http://localhost:5000/api/admin/login', {
                method: 'POST',
                body: formData
            });

            const data = await response.json();
            if (response.ok) {
                navigate('/voter-dashboard'); // Redirect on success
            } else {
                setError(data.message || 'Login failed.');
            }
        } catch (error) {
            setError('Server error. Try again.');
        }
    };

    // Convert Base64 to Blob
    const dataURLtoBlob = (dataURL) => {
        const byteString = atob(dataURL.split(',')[1]);
        const mimeString = dataURL.split(',')[0].split(':')[1].split(';')[0];
        const arrayBuffer = new ArrayBuffer(byteString.length);
        const uint8Array = new Uint8Array(arrayBuffer);
        for (let i = 0; i < byteString.length; i++) {
            uint8Array[i] = byteString.charCodeAt(i);
        }
        return new Blob([arrayBuffer], { type: mimeString });
    };

    return (
        <div>
            <h2>Voter Login</h2>
            <input
                type="text"
                placeholder="Enter Voter ID"
                value={voterId}
                onChange={(e) => setVoterId(e.target.value)}
            />
            <video ref={videoRef} autoPlay playsInline />
            <button onClick={startCamera}>Start Camera</button>
            <button onClick={handleLogin}>Login with Face</button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default Login;
