import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = "http://localhost:5000/api/candidates";

export default function CandidateDashboard() {
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get(API_URL);
      setCandidates(res.data.candidates || []);
    } catch (error) {
      console.error("Error fetching candidates:", error);
    }
  };

  const handleVote = async (candidateId) => {
    try {
      await axios.post(`${API_URL}/vote`, { candidateId });
      alert("Vote cast successfully!");
    } catch (error) {
      console.error("Error voting:", error);
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Candidate Dashboard</h1>
      <ul>
        {candidates.map((candidate) => (
          <li key={candidate._id} className="border p-2 mb-2 flex justify-between">
            <div>
              <p>{candidate.name}</p>
              <img src={`http://localhost:5000/uploads/${candidate.image}`} alt="Candidate" className="w-20 h-20 mt-2" />
            </div>
            <button onClick={() => handleVote(candidate._id)} className="bg-green-500 text-white p-2">
              Vote
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
