import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import './Login.css'; // Import the CSS file for styling
import axios from 'axios';


const Login = () => {
    const [id, setId] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize useNavigate

    const handleLogin = async(e) => {
        e.preventDefault();
        
        // Check if the ID is for admin or voter
        if (id === '8143796138') {
            // Redirect to admin dashboard
            navigate('/admin-dashboard');
        } else {
            try {
                // Send ID to the backend for validation & face recognition
                const response = await axios.post("http://localhost:5000/api/auth/login", { id });

                if (response.data.success) {
                    navigate("/voter-dashboard"); // Redirect to voter dashboard
                } else {
                    setError(response.data.message);
                }
            } catch (error) {
                setError("Authentication failed. Please try again.");
                console.error("Login error:", error);
            }
        }
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <input
                            type="text"
                            placeholder="Enter ID"
                            value={id}
                            onChange={(e) => setId(e.target.value)}
                            required
                        />
                    </div>
                    {error && <div className="error-message">{error}</div>}
                    <button type="submit" className="login-button">
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Login;
