import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const API_URL = "http://localhost:5000/api";

const VoterDashboard = ({ voter_id }) => {
  const [candidates, setCandidates] = useState([]);
  const [voteSuccess, setVoteSuccess] = useState("");
  const [error, setError] = useState("");
  const [hasVoted, setHasVoted] = useState(false);
  const [isVoting, setIsVoting] = useState(false);

  /** ‚úÖ Check if the voter has already voted */
  const checkVoterStatus = useCallback(async () => {
    if (!voter_id) {
      setError("‚ùå Voter ID is required.");
      return;
    }
    try {
      console.log("Checking voter status for:", voter_id); // Debugging
      const res = await axios.post(`${API_URL}/check-voter`, { voter_id });
      console.log("Voter check response:", res.data); // Log response

      if (res.data.success === false) {
        setHasVoted(true);
        setVoteSuccess(res.data.message);
      }
    } catch (error) {
      console.error("Error checking voter status:", error?.response?.data || error);
      setError("‚ùå Failed to check voter status. Please try again.");
    }
  }, [voter_id]);

  /** ‚úÖ Fetch available candidates */
  const fetchCandidates = useCallback(async () => {
    try {
      const res = await axios.get(`${API_URL}/candidates`);
      setCandidates(res.data || []);
    } catch (error) {
      console.error("Error fetching candidates:", error);
      setError("‚ùå Failed to load candidates. Please refresh.");
    }
  }, []);

  useEffect(() => {
    if (voter_id) {
      checkVoterStatus();
      fetchCandidates();
    }
  }, [voter_id, checkVoterStatus, fetchCandidates]);

  /** ‚úÖ Handle Vote Submission */
  const handleVote = async (candidateId) => {
    if (!voter_id) {
      setError("‚ùå Voter ID is required to vote.");
      return;
    }
    if (isVoting || hasVoted) return;
    setIsVoting(true);
    try {
      const res = await axios.post(`${API_URL}/vote`, { voter_id, candidateId });
      if (res.data.success) {
        setVoteSuccess("‚úÖ Your vote has been recorded!");
        setHasVoted(true);
      } else {
        setError(res.data.message || "‚ùå Unable to cast vote.");
      }
    } catch (error) {
      console.error("Error casting vote:", error);
      setError("‚ùå Error casting vote. Please try again.");
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">üó≥Ô∏è Online Voting System</h1>

      {error && <p className="error-message">{error}</p>}
      {voteSuccess && <p className="success-message">{voteSuccess}</p>}

      {hasVoted ? (
        <p className="already-voted">‚ö†Ô∏è Thanks for voting! You cannot vote again.</p>
      ) : (
        <div className="candidate-list">
          {candidates.length > 0 ? (
            candidates.map((candidate) => (
              <div key={candidate._id} className="candidate-row">
                <div className="candidate-info">
                  <img
                    src={`http://localhost:5000/uploads/${candidate.logo}`}
                    alt={candidate.name}
                    className="candidate-logo"
                  />
                  <p className="candidate-name">{candidate.name}</p>
                  <p className="vote-count">Votes: {candidate.votes ?? 0}</p>
                  <button
                    className="vote-button"
                    onClick={() => handleVote(candidate._id)}
                    disabled={isVoting}
                  >
                    {isVoting ? "Voting..." : "üó≥Ô∏è Vote"}
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p className="no-candidates">‚ö†Ô∏è No candidates available.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default VoterDashboard;
