import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [voterId, setVoterId] = useState("");
  const [isVerified, setIsVerified] = useState(false);

  useEffect(() => {
    // Retrieve voter_id from localStorage
    const storedVoterId = localStorage.getItem("voter_id");
    if (storedVoterId) {
      setVoterId(storedVoterId);
      verifyVoter(storedVoterId);
    }
  }, []);

  const verifyVoter = async (id) => {
    try {
      const response = await axios.post("http://localhost:5000/api/voter/check-voter", { voterId: id });

      if (response.data.success) {
        setIsVerified(true);
        fetchCandidates(); // Load candidates after successful verification
      } else {
        alert("‚ùå Invalid Voter ID. Please log in again.");
        localStorage.removeItem("voter_id"); // Clear localStorage if invalid
      }
    } catch (error) {
      console.error("‚ùå Error verifying voter:", error);
      alert("Error verifying voter. Try again!");
    }
  };

  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      console.log("üü¢ Candidates Data:", response.data);
      setCandidates(response.data);
    } catch (error) {
      console.error("‚ùå Error fetching candidates:", error);
    }
  };

  const handleVote = async (candidateId) => {
    if (!isVerified) {
      alert("‚ö†Ô∏è Please verify your Voter ID before voting.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/vote", { candidateId });
      console.log("‚úÖ Vote Successful:", response.data);
      alert("üéâ Vote cast successfully!");
    } catch (error) {
      console.error("‚ùå Error voting:", error);
      alert("Error casting vote. Try again!");
    }
  };

  return (
    <div className="voter-dashboard">
      <h2>üó≥ Voter Dashboard</h2>

      {isVerified ? (
        <>
          {/* Candidate List (Only Show After Verification) */}
          {candidates.length === 0 ? (
            <p>Loading candidates...</p>
          ) : (
            <div className="candidates-container">
              {candidates.map((candidate) => (
                <div key={candidate._id} className="candidate-card">
                  <h3>{candidate.name} ({candidate.party})</h3>
                  <img 
                    src={`http://localhost:5000/uploads/${candidate.logo}`} 
                    alt={candidate.name} 
                  />
                  <button onClick={() => handleVote(candidate._id)}>Vote</button>
                </div>
              ))}
            </div>
          )}
        </>
      ) : (
        <p>Verifying voter...</p>
      )}
    </div>
  );
};

export default VoterDashboard;
