import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);

  const verifyVoter = useCallback(async () => {
    const storedVoterId = localStorage.getItem("voter_id"); // Get voterId from localStorage
  
    if (!storedVoterId) {
      alert("‚ùå No Voter ID found. Please log in again.");
      return;
    }
  
    console.log("üîç Sending Voter ID:", storedVoterId); // Debugging log
  
    try {
      const response = await axios.post(
        "http://localhost:5000/api/voter/check-voter",
        { voterId: storedVoterId }, // Ensure voterId is correctly sent in request body
        { headers: { "Content-Type": "application/json" } } // Set headers explicitly
      );
  
      console.log("‚úÖ API Response:", response.data); // Debugging log
  
      if (response.data.success) {
        setIsVerified(true);
        fetchCandidates(); // Load candidates after verification
      } else {
        alert("‚ùå Invalid Voter ID. Please log in again.");
        localStorage.removeItem("voter_id");
      }
    } catch (error) {
      console.error("‚ùå Error verifying voter:", error.response?.data || error.message);
      alert("Error verifying voter. Try again!");
    }
  }, []);
  

  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      console.log("üü¢ Candidates Data:", response.data);
      setCandidates(response.data);
    } catch (error) {
      console.error("‚ùå Error fetching candidates:", error);
    }
  };

  useEffect(() => {
    verifyVoter(); // Auto-verify voter on component mount
  }, [verifyVoter]);

  const handleVote = async (candidateId) => {
    if (!isVerified) {
      alert("‚ö†Ô∏è Please verify your Voter ID before voting.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/vote", {
        voterId: localStorage.getItem("voter_id"), // Send voter_id with vote
        candidateId,
      });

      console.log("‚úÖ Vote Successful:", response.data);
      alert("üéâ Vote cast successfully!");
    } catch (error) {
      console.error("‚ùå Error voting:", error.response?.data || error.message);
      alert("Error casting vote. Try again!");
    }
  };

  return (
    <div className="voter-dashboard">
      <h2>üó≥ Voter Dashboard</h2>

      {isVerified ? (
        <div className="candidates-container">
          {candidates.length === 0 ? (
            <p>Loading candidates...</p>
          ) : (
            candidates.map((candidate) => (
              <div key={candidate._id} className="candidate-card">
                <h3>{candidate.name} ({candidate.party})</h3>
                <img src={`http://localhost:5000/uploads/${candidate.logo}`} alt={candidate.name} />
                <button onClick={() => handleVote(candidate._id)}>Vote</button>
              </div>
            ))
          )}
        </div>
      ) : (
        <p>Verifying Voter ID...</p>
      )}
    </div>
  );
};

export default VoterDashboard;
