import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const VoterDashboard = ({ voter_id }) => {
  const [candidates, setCandidates] = useState([]);
  const [hasVoted, setHasVoted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [voting, setVoting] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      if (!voter_id) return; // ‚úÖ Prevents API call with undefined voter_id

      try {
        const [candidatesRes, voterStatusRes] = await Promise.all([
          axios.get(`${API_URL}/candidates?timestamp=${new Date().getTime()}`),
          axios.post(`${API_URL}/voter/check-voter`, { voter_id }, { 
            headers: { "Content-Type": "application/json" } 
          }),
        ]);

        setCandidates(candidatesRes.data.candidates || []);

        if (!voterStatusRes.data.success) {
          setHasVoted(true);
          setSuccessMessage("‚úÖ You have already voted. Thank you!");
        }

        setLoading(false);
      } catch (error) {
        console.error("‚ùå Error loading data:", error);
        setError("‚ùå Failed to load data. Please refresh.");
        setLoading(false);
      }
    };

    fetchData();
  }, [voter_id]); // ‚úÖ Correct dependency array

  const handleVote = async (candidateName) => {
    if (hasVoted || voting) return;

    setVoting(true);
    setError("");
    setSuccessMessage("");

    try {
      const response = await axios.post(`${API_URL}/vote`, {
        voter_id, // ‚úÖ Corrected voter_id usage
        candidateName,
      });

      if (response.data.success) {
        setHasVoted(true);
        setSuccessMessage("üéâ Your vote has been successfully recorded!");
      } else {
        setError(response.data.message);
      }
    } catch (error) {
      console.error("‚ùå Error submitting vote:", error);
      setError("‚ùå Failed to submit vote. Please try again.");
    } finally {
      setVoting(false);
    }
  };

  return (
    <div>
      <h2>üó≥ Voter Dashboard</h2>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}

      {loading ? (
        <p>Loading candidates...</p>
      ) : hasVoted ? (
        <p>‚úÖ You have already voted. Thank you for participating!</p>
      ) : (
        <div>
          <h3>Candidates</h3>
          {candidates.length === 0 ? (
            <p>‚ö†Ô∏è No candidates available.</p>
          ) : (
            candidates.map((candidate) => (
              <div key={candidate._id} style={{ marginBottom: "10px" }}>
                <p>{candidate.name}</p>
                <button
                  onClick={() => handleVote(candidate.name)}
                  disabled={voting}
                >
                  {voting ? "Voting..." : "Vote"}
                </button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default VoterDashboard;
