import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CandidateManagement.css'; // Import CSS for styling

const CandidateManagement = () => {
  const [candidates, setCandidates] = useState([]);
  const [candidateName, setCandidateName] = useState('');
  const [party, setParty] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  // For updating a candidate
  const [editCandidateId, setEditCandidateId] = useState(null);
  const [editCandidateName, setEditCandidateName] = useState('');
  const [editParty, setEditParty] = useState('');
  const [editImage, setEditImage] = useState(null);

  // Fetch candidate list on component mount
  useEffect(() => {
    fetchCandidates();
  }, []);

  // Function to fetch all candidates
  const fetchCandidates = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/candidates');
      setCandidates(response.data);
    } catch (error) {
      console.error('Error fetching candidates:', error);
      setMessage('Error fetching candidates.');
    }
  };

  // Handle adding a new candidate
  const handleAddCandidate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', candidateName);
    formData.append('party', party);
    if (image) {
      formData.append('image', image);
    }

    try {
      await axios.post('http://localhost:5000/api/candidates/add', formData);
      setMessage('Candidate added successfully!');
      fetchCandidates();
      setCandidateName('');
      setParty('');
      setImage(null);
    } catch (error) {
      console.error('Error adding candidate:', error);
      setMessage('Error adding candidate.');
    }
  };

  // Handle deleting a candidate
  const handleDeleteCandidate = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/candidates/delete/${id}`);
      setMessage('Candidate deleted successfully!');
      fetchCandidates();
    } catch (error) {
      console.error('Error deleting candidate:', error);
      setMessage('Error deleting candidate.');
    }
  };

  // Show update form when clicking "Update"
  const handleEditClick = (candidate) => {
    setEditCandidateId(candidate._id);
    setEditCandidateName(candidate.name);
    setEditParty(candidate.party);
    setEditImage(null); // Reset the image field
  };

  // Cancel update
  const handleCancelEdit = () => {
    setEditCandidateId(null);
    setEditCandidateName('');
    setEditParty('');
    setEditImage(null);
  };

  // Handle updating a candidate
  const handleUpdateCandidate = async (e) => {
    e.preventDefault();

    if (!editCandidateId) {
      setMessage('No candidate selected to update.');
      return;
    }

    const formData = new FormData();
    formData.append('name', editCandidateName);
    formData.append('party', editParty);
    if (editImage) {
      formData.append('image', editImage);
    }

    try {
      await axios.put(`http://localhost:5000/api/candidates/update/${editCandidateId}`, formData);
      setMessage('Candidate updated successfully!');
      fetchCandidates();
      handleCancelEdit();
    } catch (error) {
      console.error('Error updating candidate:', error);
      setMessage('Error updating candidate.');
    }
  };

  return (
    <div className="candidate-management">
      <h2>Candidate Management</h2>
      {message && <div className="message">{message}</div>}

      {/* Form for adding a new candidate */}
      <form onSubmit={handleAddCandidate}>
        <div className="form-group">
          <input
            type="text"
            placeholder="Enter Candidate Name"
            value={candidateName}
            onChange={(e) => setCandidateName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <input
            type="text"
            placeholder="Enter Party Name"
            value={party}
            onChange={(e) => setParty(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>

        <button type="submit" className="add-candidate-button">
          Add Candidate
        </button>
      </form>

      <h3>Candidate List</h3>
      <div className="candidate-list">
        {candidates.map((candidate) => (
          <div key={candidate._id} className="candidate-card">
            <img
              src={candidate.image_filename 
                ? `http://localhost:5000/uploads/${candidate.image_filename}` 
                : 'http://localhost:5000/uploads/default.jpg'}
              alt="Candidate"
              className="candidate-image"
            />
            <h4>{candidate.name}</h4>
            <p>{candidate.party}</p>
            <button
              className="delete-button"
              onClick={() => handleDeleteCandidate(candidate._id)}
            >
              Delete
            </button>
            <button
              className="update-button"
              onClick={() => handleEditClick(candidate)}
            >
              Update
            </button>
          </div>
        ))}
      </div>

      {/* Update Form (only visible if editCandidateId is set) */}
      {editCandidateId && (
        <div className="update-form">
          <h3>Update Candidate</h3>
          <form onSubmit={handleUpdateCandidate}>
            <div className="form-group">
              <label>Candidate Name:</label>
              <input
                type="text"
                value={editCandidateName}
                onChange={(e) => setEditCandidateName(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Party Name:</label>
              <input
                type="text"
                value={editParty}
                onChange={(e) => setEditParty(e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>New Image (optional):</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setEditImage(e.target.files[0])}
              />
            </div>

            <div className="button-container">
              <button type="submit" className="save-button">
                Save
              </button>
              <button type="button" className="cancel-button" onClick={handleCancelEdit}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default CandidateManagement;
