import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const API_URL = "http://localhost:5000/api"; // ‚úÖ API base URL

const VoterDashboard = ({ voter_id }) => {
  const [candidates, setCandidates] = useState([]); // ‚úÖ Store fetched candidates
  const [hasVoted, setHasVoted] = useState(false); // ‚úÖ Track if voter has voted
  const [isVoting, setIsVoting] = useState(false); // ‚úÖ Prevent multiple clicks
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // ‚úÖ Fetch candidates from API
  const fetchCandidates = async () => {
    try {
      const res = await axios.get(`${API_URL}/candidates`);
      setCandidates(res.data || []); // ‚úÖ Ensure array format
    } catch (error) {
      console.error("‚ùå Error fetching candidates:", error);
      setError("‚ùå Failed to load candidates. Please refresh.");
    }
  };

  // ‚úÖ Check if the voter has already voted
  useEffect(() => {
    if (!voter_id) {
      setError("‚ùå Voter ID is required.");
      return;
    }

    const checkVoterStatus = async () => {
      try {
        const res = await axios.post(`${API_URL}/check-voter`, { voter_id });
        
        if (!res.data.success) {
          setHasVoted(true); // ‚úÖ Mark voter as already voted
          setSuccessMessage("‚úÖ You have already voted. Thank you!");
        }
      } catch (error) {
        console.error("‚ùå Error checking voter status:", error);
        setError("‚ùå Failed to check voter status. Please try again.");
      }
    };

    checkVoterStatus();
    fetchCandidates();
  }, [voter_id]); // ‚úÖ Dependency array prevents infinite re-renders

  // ‚úÖ Handle voting logic
  const handleVote = async (candidateId, candidateName) => {
    if (hasVoted || isVoting) return; // ‚úÖ Prevent multiple votes

    setIsVoting(true);
    setError("");
    setSuccessMessage("");

    try {
      // ‚úÖ Step 1: Cast vote in the backend (MongoDB)
      await axios.post(`${API_URL}/vote`, { voter_id, candidateName });

      // ‚úÖ Step 2: Update vote count in Google Sheets (Handled in backend)
      await axios.post(`${API_URL}/updateGoogleSheets`, { candidateName });

      // ‚úÖ Step 3: Update UI to reflect vote
      setCandidates((prevCandidates) =>
        prevCandidates.map((c) =>
          c._id === candidateId ? { ...c, votes: (c.votes || 0) + 1 } : c
        )
      );

      // ‚úÖ Step 4: Mark voter as voted
      setHasVoted(true);
      setSuccessMessage(`‚úÖ Your vote for ${candidateName} has been recorded successfully!`);
    } catch (error) {
      console.error("‚ùå Error voting:", error);
      setError("‚ùå Failed to cast vote. Try again.");
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">üó≥Ô∏è Online Voting System</h1>

      {error && <p className="error-message">{error}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}

      <div className="candidate-list">
        {candidates.length > 0 ? (
          candidates.map((candidate) => (
            <div key={candidate._id} className="candidate-row">
              {/* Candidate Logo & Info */}
              <div className="candidate-info">
                <img
                  src={`http://localhost:5000/uploads/${candidate.logo}`}
                  alt={candidate.name}
                  className="candidate-logo"
                />
                <p className="candidate-name">{candidate.name}</p>
                <p className="vote-count">Votes: {candidate.votes || 0}</p>
              </div>

              {/* Vote Button */}
              <button
                className={`vote-button ${hasVoted ? "disabled" : ""}`}
                onClick={() => handleVote(candidate._id, candidate.name)}
                disabled={hasVoted || isVoting}
              >
                {hasVoted ? "‚úÖ Voted" : isVoting ? "Voting..." : "üó≥Ô∏è Vote"}
              </button>
            </div>
          ))
        ) : (
          <p className="no-candidates">‚ö†Ô∏è No candidates available.</p>
        )}
      </div>
    </div>
  );
};

export default VoterDashboard;
