import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [voterId, setVoterId] = useState('');
    const [image, setImage] = useState(null);
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/users'); // Adjusted endpoint to fetch all users
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
            setMessage('Error fetching users.');
        }
    };

    const handleAddUser  = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('voter_id', voterId);
        if (image) {
            formData.append('image', image);
        }

        try {
            await axios.post('http://localhost:5000/api/users/addvoter', formData);
            setMessage('User  added successfully!');
            fetchUsers();
            setVoterId(''); // Clear the input field
            setImage(null); // Clear the image input
        } catch (error) {
            console.error('Error adding user:', error);
            setMessage('Error adding user.');
        }
    };

    const handleDeleteUser  = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/users/delete/${id}`);
            setMessage('User  deleted successfully!');
            fetchUsers();
        } catch (error) {
            console.error('Error deleting user:', error);
            setMessage('Error deleting user.');
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">User  Management</h2>
            {message && <div className="alert alert-info">{message}</div>}
            <form onSubmit={handleAddUser }>
                <div className="form-group">
                    <label htmlFor="voterId">Voter ID</label>
                    <input
                        type="text"
                        className="form-control"
                        id="voterId"
                        value={voterId}
                        onChange={(e) => setVoterId(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="image">Image</label>
                    <input
                        type="file"
                        className="form-control"
                        id="image"
                        onChange={(e) => setImage(e.target.files[0])}
                    />
                </div>
                <button type="submit" className="btn btn-primary">Add User</button>
            </form>

            <h3 className="mt-5">User  List</h3>
            <ul className="list-group">
                {users.map((user) => (
                    <li key={user._id} className="list-group-item d-flex justify-content-between align-items-center">
                        {user.voter_id}
                        <button className="btn btn-danger" onClick={() => handleDeleteUser (user._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserManagement;
