import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Login = () => {
    const [id, setId] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError(''); // Clear previous errors

        if (!id.trim()) {
            setError("Please enter your Voter ID.");
            return;
        }

        if (id === '8143796138') {
            // ✅ Admin Login - Redirect to Admin Dashboard
            localStorage.setItem("admin_id", id);
            navigate('/admin-dashboard');
        } else {
            try {
                // ✅ Send voter_id to backend for authentication
                const response = await axios.post("http://localhost:5000/api/users/login", { voter_id: id });

                if (response.data.success) {
                    // ✅ Store voter_id in localStorage
                    localStorage.setItem("voter_id", id);

                    // ✅ Redirect to Voter Dashboard
                    navigate("/voter-dashboard");
                } else {
                    setError(response.data.message); // Show backend error message
                }
            } catch (error) {
                setError("Authentication failed. Please try again.");
                console.error("Login error:", error);
            }
        }
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <h2>Online Voting System</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <input
                            type="text"
                            placeholder="Enter your Voter ID"
                            value={id}
                            onChange={(e) => setId(e.target.value)}
                            required
                        />
                    </div>
                    {error && <div className="error-message">{error}</div>}
                    <button type="submit" className="login-button">
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Login;
