import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [isVerified, setIsVerified] = useState(false);
  const [hasVoted, setHasVoted] = useState(localStorage.getItem("hasVoted") === "true");
  const [isLoading, setIsLoading] = useState(true);

  const voterId = localStorage.getItem("voter_id");
  
  useEffect(() => {
    const verifyVoter = async () => {
      if (!voterId) {
        console.error("❌ No voter ID found.");
        setIsLoading(false);
        return;
      }
  
      try {
        console.log(`🔍 Checking voter ID: ${voterId}`);
        const response = await axios.get(
          `http://localhost:5000/api/voter/check-vote/${voterId}`
        );
  
        console.log("✅ API Response:", response.data);
  
        if (response.data.success) {
          if (response.data.hasVoted) {
            setHasVoted(true);
            localStorage.setItem("hasVoted", "true");
            setIsVerified(false); // Prevent voting again
          } else {
            setHasVoted(false);
            localStorage.removeItem("hasVoted"); // Reset if they haven't voted
            setIsVerified(true); // Allow voting
            fetchCandidates();
          }
        } else {
          console.error("❌ Voter verification failed:", response.data.message);
          setIsVerified(false);
        }
      } catch (error) {
        console.error("❌ Error verifying voter:", error.response?.data || error.message);
        setIsVerified(false);
      } finally {
        setIsLoading(false);
      }
    };
  
    verifyVoter();
  }, [voterId]);
  
  
  

  const fetchCandidates = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/candidates");
      setCandidates(response.data);
    } catch (error) {
      console.error("❌ Error fetching candidates:", error);
    }
  };

  const handleVote = async (candidate) => {
    if (!candidate) {
      alert("⚠️ Please select a candidate first!");
      return;
    }
  
    try {
      console.log(`🗳 Voting for Candidate: ${candidate.name} (ID: ${candidate._id})`);
  
      const response = await axios.post("http://localhost:5000/api/voter/vote", {
        voterId,
        candidateId: candidate._id,
        candidateName: candidate.name,
      });
  
      console.log("📨 Vote Response:", response.data);
  
      if (response.data.success) {
        alert("🎉 Vote cast successfully!");
        setHasVoted(true);
        localStorage.setItem("hasVoted", "true"); // Persist voting status
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      console.error("❌ Error voting:", error.response?.data || error.message);
    }
  };
  

  return (
    <div className="voter-dashboard">
      <h2>🗳 Online Voting System</h2>
  
      {isLoading ? (
        <p>⏳ Verifying Voter ID...</p>
      ) : hasVoted ? (
        <h3>✅ Thanks for Voting!</h3> // ✅ Always shows if voter has already voted
      ) : isVerified ? (
        <div>
          <h3>Select a Candidate:</h3>
          {candidates.length === 0 ? (
            <p>⏳ Loading candidates...</p>
          ) : (
            <div className="candidates-container">
              {candidates.map((candidate) => (
                <div className="candidate-row" key={candidate._id}>
                  <img
                    src={`http://localhost:5000/uploads/${candidate.logo}`}
                    alt={candidate.name}
                    className="candidate-logo"
                  />
                  <div className="candidate-info">
                    <h3>{candidate.name}</h3>
                    <p>Party: {candidate.party}</p>
                  </div>
                  <button onClick={() => handleVote(candidate)} className="vote-button">
                    Vote
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      ) : (
        <p>❌ Verification Failed. Please try again.</p>
      )}
    </div>
  );
  
  
  
};

export default VoterDashboard;
