import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Candidate.css";

const CandidateManagement = () => {
    const [candidates, setCandidates] = useState([]);
    const [editingId, setEditingId] = useState(null);
    const [editName, setEditName] = useState("");
    const [editParty, setEditParty] = useState("");
    const [editLogo, setEditLogo] = useState(null);

    // Fetch candidates from API
    useEffect(() => {
        fetchCandidates();
    }, []);

    const fetchCandidates = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/candidates");
            setCandidates(response.data);
        } catch (error) {
            console.error("Error fetching candidates:", error);
        }
    };

    // Handle file selection for editing
    const handleFileChange = (event) => {
        setEditLogo(event.target.files[0]);
    };

    // Save edited candidate
    const handleSaveEdit = async (id) => {
        const formData = new FormData();
        formData.append("name", editName);
        formData.append("party", editParty);
        if (editLogo) formData.append("logo", editLogo);

        try {
            await axios.put(`http://localhost:5000/api/candidates/update/${id}`, formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            fetchCandidates(); // Refresh list
            setEditingId(null); // Exit edit mode
        } catch (error) {
            console.error("Error updating candidate:", error);
        }
    };

    return (
        <div className="candidate-management">
            <h2 className="title">Candidate Management</h2>

            <ul className="candidate-list">
                {candidates.map((candidate) => (
                    <li key={candidate._id} className="candidate-item">
                        <div className="candidate-info-container">
                            <img
                                src={`http://localhost:5000/uploads/${candidate.logo}`}
                                alt={candidate.name}
                                className="candidate-logo"
                                crossOrigin="anonymous"
                                onError={(e) => (e.target.src = "/default-logo.png")}
                            />

                            <div className="candidate-details">
                                <h4 className="candidate-name">{candidate.name}</h4>
                                <p className="candidate-party">{candidate.party}</p>
                            </div>
                        </div>

                        <div className="button-group">
                            <button onClick={() => {
                                setEditingId(candidate._id);
                                setEditName(candidate.name);
                                setEditParty(candidate.party);
                            }} className="edit-button">
                                Edit
                            </button>
                            <button onClick={() => handleDeleteCandidate(candidate._id)} className="delete-button">
                                Delete
                            </button>
                        </div>

                        {/* Render Edit Form Below Candidate if Editing */}
                        {editingId === candidate._id && (
                            <form className="edit-form">
                                <input
                                    type="text"
                                    value={editName}
                                    className="input-field"
                                    onChange={(e) => setEditName(e.target.value)}
                                    required
                                />
                                <input
                                    type="text"
                                    value={editParty}
                                    className="input-field"
                                    onChange={(e) => setEditParty(e.target.value)}
                                    required
                                />
                                <input type="file" accept="image/*" onChange={handleFileChange} className="file-input" />

                                <div className="button-group">
                                    <button type="button" onClick={() => handleSaveEdit(candidate._id)} className="save-button">
                                        Save
                                    </button>
                                    <button type="button" onClick={() => setEditingId(null)} className="cancel-button">
                                        Cancel
                                    </button>
                                </div>
                            </form>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CandidateManagement;
