import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css"; // ✅ Import CSS file

const API_URL = "http://localhost:5000/api"; // ✅ Adjust API URL

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]); // ✅ Store fetched candidates
  const [votedCandidate, setVotedCandidate] = useState(null);
  const [isVoting, setIsVoting] = useState(false); // ✅ Prevent multiple clicks

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get(`${API_URL}/candidates`); // ✅ Fetch candidates
      setCandidates(res.data || []); // ✅ Ensure it's always an array
    } catch (error) {
      console.error("Error fetching candidates:", error);
      setCandidates([]); // ✅ Prevent undefined state
    }
  };

  const handleVote = async (candidate) => {
    if (isVoting || votedCandidate) return; // ✅ Prevent multiple votes
    setIsVoting(true);

    try {
      // 1️⃣ ✅ Cast vote in the backend (MongoDB)
      await axios.post(`${API_URL}/vote`, { candidateId: candidate._id });

      // 2️⃣ ✅ Update vote count in Google Sheets (Backend will handle logic)
      await axios.post(`${API_URL}/updateGoogleSheets`, {
        candidateName: candidate.name,
      });

      // 3️⃣ ✅ Mark as voted
      setVotedCandidate(candidate._id);
      alert(`Your vote for ${candidate.name} has been recorded successfully!`);
    } catch (error) {
      console.error("Error voting:", error);
      alert("Failed to cast vote. Try again.");
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">Online Voting System</h1>

      <div className="candidate-list">
        {candidates.length > 0 ? (
          candidates.map((candidate) => (
            <div key={candidate._id} className="candidate-row">
              {/* Left: Party Logo & Name */}
              <div className="candidate-info">
                <img
                  src={`http://localhost:5000/uploads/${candidate.logo}`}
                  alt={candidate.name}
                  className="candidate-logo"
                />
                <p className="candidate-name">{candidate.name}</p>
              </div>

              {/* Right: Vote Button */}
              <button
                className={`vote-button ${votedCandidate ? "disabled" : ""}`}
                onClick={() => handleVote(candidate)}
                disabled={votedCandidate !== null || isVoting}
              >
                {votedCandidate === candidate._id ? "Voted" : "Vote"}
              </button>
            </div>
          ))
        ) : (
          <p className="no-candidates">No candidates available.</p>
        )}
      </div>
    </div>
  );
};

export default VoterDashboard;
