import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Candidate.css";

const CandidateManagement = () => {
    const [candidates, setCandidates] = useState([]);
    const [name, setName] = useState("");
    const [party, setParty] = useState("");
    const [logo, setLogo] = useState(null);
    const [editingId, setEditingId] = useState(null);
    const [message, setMessage] = useState("");

    // Fetch candidates on load
    useEffect(() => {
        fetchCandidates();
    }, []);

    const fetchCandidates = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/candidates");
            setCandidates(response.data);
        } catch (error) {
            console.error("Error fetching candidates:", error);
            setMessage("Error fetching candidates.");
        }
    };

    const handleFileChange = (event) => {
        setLogo(event.target.files[0]);
    };

    const handleAddCandidate = async (event) => {
        event.preventDefault();
        if (!name || !party) {
            alert("Please fill all fields.");
            return;
        }

        const formData = new FormData();
        formData.append("name", name);
        formData.append("party", party);
        if (logo) formData.append("logo", logo);

        try {
            await axios.post("http://localhost:5000/api/candidates/addCandidate", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            setMessage("Candidate added successfully!");
            fetchCandidates();
            setName("");
            setParty("");
            setLogo(null);

            setTimeout(() => setMessage(""), 3000);
        } catch (error) {
            console.error("Error adding candidate:", error.response?.data || error.message);
        }
    };

    const handleUpdateCandidate = async (id) => {
        if (!name || !party) {
            alert("Please fill all fields.");
            return;
        }

        const formData = new FormData();
        formData.append("name", name);
        formData.append("party", party);
        if (logo) formData.append("logo", logo);

        try {
            await axios.put(`http://localhost:5000/api/candidates/update/${id}`, formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });

            setMessage("Candidate updated successfully!");
            fetchCandidates();
            setEditingId(null);
            setName("");
            setParty("");
            setLogo(null);

            setTimeout(() => setMessage(""), 3000);
        } catch (error) {
            console.error("Error updating candidate:", error.response?.data || error.message);
        }
    };

    const handleDeleteCandidate = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/candidates/delete/${id}`);
            fetchCandidates();
            setMessage("Candidate deleted successfully!");

            setTimeout(() => setMessage(""), 3000);
        } catch (error) {
            console.error("Error deleting candidate:", error.response?.data || error.message);
        }
    };

    const handleEditCandidate = (candidate) => {
        setEditingId(candidate._id);
        setName(candidate.name);
        setParty(candidate.party);
    };

    return (
        <div className="candidate-management">
            <h2 className="title">Candidate Management</h2>

            {message && <div className="message">{message}</div>}

            {/* Add Candidate Form */}
            {!editingId && (
                <form onSubmit={handleAddCandidate} className="candidate-form">
                    <input
                        type="text"
                        placeholder="Candidate Name"
                        value={name}
                        className="input-field"
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Party Name"
                        value={party}
                        className="input-field"
                        onChange={(e) => setParty(e.target.value)}
                        required
                    />
                    <input type="file" accept="image/*" onChange={handleFileChange} className="file-input" />
                    <button type="submit" className="add-button">Add Candidate</button>
                </form>
            )}

            {/* Candidate List */}
            <ul className="candidate-list">
                {candidates.map((candidate) => (
                    <li key={candidate._id} className="candidate-item">
                        <div className="candidate-info-container">
                            <img
                                src={`http://localhost:5000/uploads/${candidate.logo}`}
                                alt={candidate.name}
                                className="candidate-logo"
                                crossOrigin="anonymous"
                                onError={(e) => {
                                    e.target.src = "/default-logo.png";
                                    console.error("Error loading candidate logo:", e.target.src);
                                }}
                            />

                            <div className="candidate-details">
                                <h4 className="candidate-name">{candidate.name}</h4>
                                <p className="candidate-party">{candidate.party}</p>
                            </div>
                        </div>

                        <div className="button-group">
                            <button onClick={() => handleEditCandidate(candidate)} className="update-button">
                                Update
                            </button>
                            <button onClick={() => handleDeleteCandidate(candidate._id)} className="delete-button">
                                Delete
                            </button>
                        </div>

                        {/* Update Form Appears Inline */}
                        {editingId === candidate._id && (
                            <form className="update-form">
                                <input
                                    type="text"
                                    value={name}
                                    className="input-field"
                                    onChange={(e) => setName(e.target.value)}
                                    required
                                />
                                <input
                                    type="text"
                                    value={party}
                                    className="input-field"
                                    onChange={(e) => setParty(e.target.value)}
                                    required
                                />
                                <input type="file" accept="image/*" onChange={handleFileChange} className="file-input" />

                                <div className="button-group">
                                    <button type="button" onClick={() => handleUpdateCandidate(candidate._id)} className="save-button">
                                        Save
                                    </button>
                                    <button type="button" onClick={() => setEditingId(null)} className="cancel-button">
                                        Cancel
                                    </button>
                                </div>
                            </form>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CandidateManagement;
