import React, { useEffect, useState } from "react";
import axios from "axios";
import "./VoterDashboard.css"
const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/candidates");
        console.log("üü¢ Candidates Data:", response.data);
        setCandidates(response.data);
      } catch (error) {
        console.error("‚ùå Error fetching candidates:", error);
      }
    };
    fetchData();
  }, []);

  const handleVote = async (candidateId) => {
    try {
      const response = await axios.post("http://localhost:5000/api/vote", {
        candidateId: candidateId,
      });
      console.log("‚úÖ Vote Successful:", response.data);
      alert("Vote cast successfully!");
    } catch (error) {
      console.error("‚ùå Error voting:", error);
      alert("Error casting vote. Try again!");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px", background: "#111", color: "#fff" }}>
      <h2>üó≥ Voter Dashboard</h2>
      {candidates.length === 0 ? (
        <p>Loading candidates...</p>
      ) : (
        <div style={{ display: "flex", justifyContent: "center", gap: "20px", flexWrap: "wrap" }}>
          {candidates.map((candidate) => (
            <div key={candidate._id} style={{ background: "#222", padding: "10px", borderRadius: "10px" }}>
              <h3>{candidate.name} ({candidate.party})</h3>
              <img 
                src={`http://localhost:5000/uploads/${candidate.logo}`} 
                alt={candidate.name} 
                width="100" 
                style={{ borderRadius: "10px" }}
              />
              <br />
              <button 
                onClick={() => handleVote(candidate._id)}
                style={{ marginTop: "10px", padding: "5px 10px", background: "blue", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}
              >
                Vote
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default VoterDashboard;
