import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css"; // ✅ Import CSS file

const API_URL = "http://localhost:5000/api"; // ✅ Adjusted API URL

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]); // ✅ Store fetched candidates
  const [voted, setVoted] = useState(false);

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    try {
      const res = await axios.get(`${API_URL}/candidates`); // ✅ Fetch from backend
      setCandidates(res.data || []); // ✅ Ensure it's always an array
    } catch (error) {
      console.error("Error fetching candidates:", error);
      setCandidates([]); // ✅ Prevent undefined state
    }
  };

  const handleVote = async (candidateId) => {
    try {
      await axios.post(`${API_URL}/vote`, { candidateId });
      setVoted(true);
      alert("Your vote has been cast successfully!");
    } catch (error) {
      console.error("Error voting:", error);
      alert("Failed to cast vote. Try again.");
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title">Electronic Voting System</h1>

      <div className="candidate-list">
        {candidates.length > 0 ? (
          candidates.map((candidate) => (
            <div key={candidate._id} className="candidate-row">
              {/* Left: Party Logo & Name */}
              <div className="candidate-info">
                <img src={`http://localhost:5000/uploads/${candidate.logo}`} alt={candidate.name} className="candidate-logo" />
               
              </div>

              {/* Right: Vote Button */}
              <button
                className={`vote-button ${voted ? "disabled" : ""}`}
                onClick={() => handleVote(candidate._id)}
                disabled={voted}
              >
                {voted ? "Voted" : "Vote"}
              </button>
            </div>
          ))
        ) : (
          <p className="no-candidates">No candidates available.</p>
        )}
      </div>
    </div>
  );
};

export default VoterDashboard; // ✅ Ensure it's exported properly
