import React, { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./VoterDashboard.css"; // Import CSS

const VoterDashboard = () => {
    const videoRef = useRef(null);
    const navigate = useNavigate();
    const [message, setMessage] = useState("Verifying face...");

    useEffect(() => {
        // Access webcam
        navigator.mediaDevices.getUserMedia({ video: true })
            .then((stream) => {
                if (videoRef.current) videoRef.current.srcObject = stream;
            })
            .catch((error) => {
                console.error("Camera access denied:", error);
                setMessage("Camera access denied.");
            });

        // Send face verification request
        verifyFace();
    }, []);

    const verifyFace = async () => {
        const voterId = localStorage.getItem("voterId"); // Retrieve voter ID

        try {
            const response = await fetch("http://localhost:5000/api/auth/verify-face", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ voterId }),
            });

            const data = await response.json();
            if (data.success) {
                setMessage("Face recognized! Redirecting to vote...");
                setTimeout(() => navigate("/vote"), 2000);
            } else {
                setMessage("Face not recognized. Try again.");
                setTimeout(() => navigate("/"), 2000); // Redirect to login if face doesn't match
            }
        } catch (error) {
            console.error("Error verifying face:", error);
            setMessage("Error verifying face.");
        }
    };

    return (
        <div className="voter-dashboard">
            <h2>Face Recognition</h2>
            <video ref={videoRef} autoPlay></video>
            <p>{message}</p>
        </div>
    );
};

export default VoterDashboard;
