import React, { useState } from "react";
import axios from "axios";
import candidates from "./Candidate"; // ✅ Import candidates from Candidate.js
import "./VoterDashboard.css"; // ✅ Import CSS file

const API_URL = "http://localhost:5000/api/votes"; // ✅ Backend API URL

export default function VoterDashboard() {
  const [voted, setVoted] = useState(false);

  const handleVote = async (candidateId) => {
    try {
      await axios.post(`${API_URL}/vote`, { candidateId });
      setVoted(true);
      alert("Your vote has been cast successfully!");
    } catch (error) {
      console.error("Error voting:", error);
      alert("Failed to cast vote. Try again.");
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title">Electronic Voting System</h1>

      <div className="candidate-list">
        {candidates.map((candidate) => (
          <div key={candidate.id} className="candidate-row">
            {/* Left: Party Logo & Name */}
            <div className="candidate-info">
              <img src={candidate.logo} alt={candidate.name} className="candidate-logo" />
              <p className="candidate-name">{candidate.name}</p>
            </div>

            {/* Right: Vote Button */}
            <button
              className={`vote-button ${voted ? "disabled" : ""}`}
              onClick={() => handleVote(candidate.id)}
              disabled={voted}
            >
              {voted ? "Voted" : "Vote"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
