import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const API_URL = "http://localhost:5000/api";

const VoterDashboard = ({ voter_id }) => {
  const [candidates, setCandidates] = useState([]);
  const [voteSuccess, setVoteSuccess] = useState("");
  const [error, setError] = useState("");
  const [hasVoted, setHasVoted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [isVoting, setIsVoting] = useState(false);

  /** ‚úÖ Ensure voter_id is valid */
  useEffect(() => {
    console.log("üü¢ Received voter_id in VoterDashboard:", voter_id);
    if (!voter_id) {
      setError("‚ùå Voter ID is missing. Please log in.");
    }
  }, [voter_id]);

  /** ‚úÖ Check if the voter has already voted */
  const checkVoterStatus = useCallback(async () => {
    if (!voter_id) return;

    try {
      console.log("üü¢ Checking voter status for:", voter_id);
      const res = await axios.post(`${API_URL}/voter/check-voter`, { voter_id });

      console.log("üü¢ Voter check response:", res.data);
      if (res.data.success === false) {
        setHasVoted(true);
        setVoteSuccess(res.data.message);
      }
    } catch (error) {
      console.error("‚ùå Error checking voter status:", error?.response?.data || error);
      setError("‚ùå Failed to check voter status. Please try again.");
    }
  }, [voter_id]);

  useEffect(() => {
    if (voter_id) {
      checkVoterStatus();
    }
  }, [voter_id, checkVoterStatus]);

  return (
    <div className="voter-container">
      <h1 className="title_1">üó≥Ô∏è Online Voting System</h1>
      {error ? <p className="error-message">{error}</p> : <p>Welcome, voter {voter_id}!</p>}
    </div>
  );
};

export default VoterDashboard;
