import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserManagement.css'; // Import the CSS file for styling

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [voterId, setVoterId] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  // Fetch the user list from the server on component mount
  useEffect(() => {
    fetchUsers();
  }, []);

  // Function to fetch all users
  const fetchUsers = async () => {
    try {
      // Adjust the endpoint to match your server route
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      setMessage('Error fetching users.');
    }
  };

  // Handle adding a new user
  const handleAddUser = async (e) => {
    e.preventDefault();

    // Use FormData to handle file upload
    const formData = new FormData();
    formData.append('voter_id', voterId);
    if (image) {
      formData.append('image', image);
    }

    try {
      // Adjust the endpoint to match your server route for adding a user
      await axios.post('http://localhost:5000/api/users/addvoter', formData);
      setMessage('User added successfully!');
      // Refresh the user list
      fetchUsers();
      // Reset form inputs
      setVoterId('');
      setImage(null);
    } catch (error) {
      console.error('Error adding user:', error);
      setMessage('Error adding user.');
    }
  };

  // Handle deleting a user by ID
  const handleDeleteUser = async (id) => {
    try {
      // Adjust the endpoint to match your server route for deleting a user
      await axios.delete(`http://localhost:5000/api/users/delete/${id}`);
      setMessage('User deleted successfully!');
      // Refresh the user list
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
      setMessage('Error deleting user.');
    }
  };

  return (
    <div className="user-management">
      <h2>User Management</h2>
      {message && <div className="message">{message}</div>}

      {/* Form for adding a new user */}
      <form onSubmit={handleAddUser}>
        <div className="form-group">
          <input
            type="text"
            placeholder="Enter Voter ID"
            value={voterId}
            onChange={(e) => setVoterId(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
          />
        </div>

        <button type="submit" className="add-user-button">
          Add User
        </button>
      </form>

      <h3>User List</h3>
      <div className="user-list">
        {users.map((user) => (
          <div key={user._id} className="user-card">
            {/* If user.image is available, show the uploaded image. Otherwise, show a default placeholder. */}
            {user.image ? (
              <img
                src={`http://localhost:5000/${user.image}`} // Adjust path if needed
                alt="User"
                className="user-image"
              />
            ) : (
              <img
                src="path/to/default/image.jpg"
                alt="Default"
                className="user-image"
              />
            )}
            <h4>{user.voter_id}</h4>
            <button
              className="delete-button"
              onClick={() => handleDeleteUser(user._id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserManagement;
