import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection

const Login = () => {
    const [id, setId] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize useNavigate

    const handleLogin = (e) => {
        e.preventDefault();
        
        // Check if the ID is for admin or voter
        if (id === '8143796138') {
            // Redirect to admin dashboard
            navigate('/admin-dashboard');
        } else {
            // Simulate a voter login (you can replace this with an actual API call)
            const isVoterValid = id.length > 0; // Replace with your actual voter ID validation logic
            if (isVoterValid) {
                // Redirect to voter dashboard
                navigate('/voter-dashboard');
            } else {
                setError('Invalid voter ID. Please try again.');
            }
        }
    };

    return (
        <div className="bg-black h-screen flex items-center justify-center">
            <div className="bg-gray-800 p-8 rounded-lg shadow-lg hover:shadow-xl transition-shadow duration-300">
                <h2 className="text-white text-2xl mb-4">Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group mb-4">
                        <input
                            type="text"
                            placeholder="Enter ID"
                            className="w-full p-2 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                            value={id}
                            onChange={(e) => setId(e.target.value)}
                            required
                        />
                    </div>
                    {error && <div className="text-red-500 mb-4">{error}</div>}
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition-colors duration-300">
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Login;
