import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const API_URL = "http://localhost:5000/api";

const VoterDashboard = ({ voter_id }) => {
  const [candidates, setCandidates] = useState([]);
  const [hasVoted, setHasVoted] = useState(false);
  const [isVoting, setIsVoting] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  useEffect(() => {
    if (!voter_id) {
      setError("❌ Voter ID is required.");
      return;
    }

    const fetchData = async () => {
      try {
        const [candidatesRes, voterStatusRes] = await Promise.all([
          axios.get(`${API_URL}/candidates`),
          axios.post(`${API_URL}/check-voter`, { voter_id }),
        ]);

        setCandidates(candidatesRes.data || []);

        if (!voterStatusRes.data.success) {
          setHasVoted(true);
          setSuccessMessage("✅ You have already voted. Thank you!");
        }
      } catch (error) {
        console.error("❌ Error loading data:", error);
        setError("❌ Failed to load data. Please refresh.");
      }
    };

    fetchData();
  }, [voter_id]);

  const handleVote = async (candidateId, candidateName) => {
    if (hasVoted || isVoting) return;

    setIsVoting(true);
    setError("");
    setSuccessMessage("");

    try {
      await Promise.all([
        axios.post(`${API_URL}/vote`, { voter_id, candidateName }),
        axios.post(`${API_URL}/updateGoogleSheets`, { candidateName }),
      ]);

      setCandidates((prev) =>
        prev.map((c) =>
          c._id === candidateId ? { ...c, votes: (c.votes || 0) + 1 } : c
        )
      );

      setHasVoted(true);
      setSuccessMessage(`✅ Your vote for ${candidateName} has been recorded successfully!`);
    } catch (error) {
      console.error("❌ Error voting:", error);
      setError("❌ Failed to cast vote. Try again.");
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">🗳️ Online Voting System</h1>

      {error && <p className="error-message">{error}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}

      <div className="candidate-list">
        {candidates.length > 0 ? (
          candidates.map((candidate) => (
            <div key={candidate._id} className="candidate-row">
              <div className="candidate-info">
                <img
                  src={`http://localhost:5000/uploads/${candidate.logo}`}
                  alt={candidate.name}
                  className="candidate-logo"
                />
                <p className="candidate-name">{candidate.name}</p>
                <p className="vote-count">Votes: {candidate.votes || 0}</p>
              </div>

              <button
                className={`vote-button ${hasVoted ? "disabled" : ""}`}
                onClick={() => handleVote(candidate._id, candidate.name)}
                disabled={hasVoted || isVoting}
              >
                {hasVoted ? "✅ Voted" : isVoting ? "Voting..." : "🗳️ Vote"}
              </button>
            </div>
          ))
        ) : (
          <p className="no-candidates">⚠️ No candidates available.</p>
        )}
      </div>
    </div>
  );
};

export default VoterDashboard;
