const express = require('express');
const cors = require('cors');
const app = express();
const PORT = 5000;

app.use(cors());
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

let users = []; // This is just an example; you would typically use a database

// GET /api/users
app.get('/api/users', (req, res) => {
    res.json(users);
});

// POST /api/users/addvoter
app.post('/api/users/addvoter', (req, res) => {
    const { voter_id } = req.body; // Assuming you send voter_id in the body
    users.push({ id: users.length + 1, voter_id }); // Add user to the array
    res.status(201).json({ message: 'User  added successfully!' });
});

app.listen(PORT, () => {
    console.log(`Server is running on http://localhost:${PORT}`);
});
