import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:5000/api/users"; // ✅ Ensure this matches your backend

export default function VoterManagement() {
  const [voters, setVoters] = useState([]); // ✅ Ensure voters is always an array
  const [voterId, setVoterId] = useState("");
  const [image, setImage] = useState(null);
  const [editingVoter, setEditingVoter] = useState(null);

  useEffect(() => {
    fetchVoters();
  }, []);

  const fetchVoters = async () => {
    try {
      const res = await axios.get(API_URL);
      console.log("API Response:", res.data); // ✅ Debug API response
      setVoters(res.data.voters || []); // ✅ Ensure it is always an array
    } catch (error) {
      console.error("Error fetching voters:", error);
      setVoters([]); // ✅ Prevent undefined state
    }
  };

  const handleAddVoter = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("voter_id", voterId);
    formData.append("image", image);

    try {
      await axios.post(`${API_URL}/addvoter`, formData);
      fetchVoters();
      setVoterId("");
      setImage(null);
    } catch (error) {
      console.error("Error adding voter:", error);
    }
  };

  const handleUpdateVoter = async (e) => {
    e.preventDefault();
    if (!editingVoter) return;

    const formData = new FormData();
    formData.append("voter_id", voterId);
    if (image) formData.append("image", image);

    try {
      await axios.put(`${API_URL}/update/${editingVoter._id}`, formData);
      fetchVoters();
      setEditingVoter(null);
      setVoterId("");
      setImage(null);
    } catch (error) {
      console.error("Error updating voter:", error);
    }
  };

  const handleDeleteVoter = async (id) => {
    try {
      await axios.delete(`${API_URL}/delete/${id}`);
      fetchVoters();
    } catch (error) {
      console.error("Error deleting voter:", error);
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Voter Management</h1>
      <form onSubmit={editingVoter ? handleUpdateVoter : handleAddVoter} className="mb-4">
        <input
          type="text"
          placeholder="Voter ID (12 digits)"
          value={voterId}
          onChange={(e) => setVoterId(e.target.value)}
          className="border p-2 w-full mb-2"
          required
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
          className="border p-2 w-full mb-2"
        />
        <button type="submit" className="bg-blue-500 text-white p-2 w-full">
          {editingVoter ? "Update Voter" : "Add Voter"}
        </button>
      </form>

      {/* ✅ Ensure voters is always an array before mapping */}
      {Array.isArray(voters) && voters.length > 0 ? (
        <ul>
          {voters.map((voter) => (
            <li key={voter._id} className="border p-2 mb-2 flex justify-between">
              <div>
                <p>Voter ID: {voter.voter_id}</p>
                {voter.image_filename && (
                  <img
                    src={`http://localhost:5000/uploads/${voter.image_filename}`}
                    alt="Voter"
                    className="w-20 h-20 mt-2"
                  />
                )}
              </div>
              <div>
                <button
                  onClick={() => {
                    setEditingVoter(voter);
                    setVoterId(voter.voter_id);
                  }}
                  className="bg-yellow-500 text-white p-1 mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteVoter(voter._id)}
                  className="bg-red-500 text-white p-1"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-center text-gray-500">No voters found.</p> // ✅ Show message if empty
      )}
    </div>
  );
}
