import React, { useState, useEffect } from "react";
import axios from "axios";
import './Candidate.';


const CandidateManagement = () => {
    const [candidates, setCandidates] = useState([]);
    const [name, setName] = useState("");
    const [party, setParty] = useState("");
    const [logo, setLogo] = useState(null);

    // ✅ Fetch all candidates
    const fetchCandidates = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/candidates");
            setCandidates(response.data);
        } catch (error) {
            console.error("Error fetching candidates:", error.response?.data || error.message);
        }
    };

    useEffect(() => {
        fetchCandidates();
    }, []);

    // ✅ Handle file selection
    const handleFileChange = (event) => {
        setLogo(event.target.files[0]);
    };

    // ✅ Add new candidate
    const handleAddCandidate = async (event) => {
        event.preventDefault();

        if (!name || !party || !logo) {
            alert("Please fill all fields and select a logo.");
            return;
        }

        const formData = new FormData();
        formData.append("name", name);
        formData.append("party", party);
        formData.append("logo", logo);

        try {
            const response = await axios.post(
                "http://localhost:5000/api/candidates/addCandidate", // ✅ Fixed URL
                formData,
                { headers: { "Content-Type": "multipart/form-data" } }
            );

            console.log("Candidate added:", response.data);
            fetchCandidates(); // Refresh the list
            setName("");
            setParty("");
            setLogo(null);
        } catch (error) {
            console.error("Error adding candidate:", error.response?.data || error.message);
        }
    };

    // ✅ Delete candidate
    const handleDeleteCandidate = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/candidates/delete/${id}`); // ✅ Fixed URL
            fetchCandidates();
        } catch (error) {
            console.error("Error deleting candidate:", error.response?.data || error.message);
        }
    };

    return (
        <div>
            <h2>Candidate Management</h2>

            {/* Form to Add Candidate */}
            <form onSubmit={handleAddCandidate}>
                <input
                    type="text"
                    placeholder="Candidate Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />
                <input
                    type="text"
                    placeholder="Party Name"
                    value={party}
                    onChange={(e) => setParty(e.target.value)}
                    required
                />
                <input type="file" accept="image/*" onChange={handleFileChange} required />
                <button type="submit">Add Candidate</button>
            </form>

            {/* Candidate List */}
            <h3>Candidate List</h3>
            <ul>
                {candidates.map((candidate) => (
                    <li key={candidate._id}>
                        {candidate.logo && (
                            <img
                                src={`http://localhost:5000/uploads/${candidate.logo}`} // ✅ Ensure your backend serves static files
                                alt={candidate.name}
                                width="50"
                                onError={(e) => (e.target.style.display = "none")} // Hide broken images
                            />
                        )}
                        <span>{candidate.name} - {candidate.party}</span>
                        <button onClick={() => handleDeleteCandidate(candidate._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CandidateManagement;
