import React, { useEffect, useState } from 'react';

const UserManagement = () => {
    const [users, setUsers] = useState([]);
    const [voterId, setVoterId] = useState('');
    const [image, setImage] = useState(null);
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/users'); // Adjust the endpoint as needed
            if (!response.ok) {
                throw new Error('Failed to fetch users');
            }
            const data = await response.json();
            setUsers(data);
        } catch (error) {
            setMessage('Error fetching users.');
            console.error('Fetch error:', error);
        }
    };

    const handleAddUser  = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('voter_id', voterId);
        if (image) {
            formData.append('image', image);
        }

        try {
            const response = await fetch('http://localhost:5000/api/users/addvoter', {
                method: 'POST',
                body: formData,
            });
            if (!response.ok) {
                throw new Error('Failed to add user');
            }
            setMessage('User  added successfully!');
            fetchUsers(); // Refresh the user list
            setVoterId(''); // Clear the input field
            setImage(null); // Clear the image input
        } catch (error) {
            setMessage('Error adding user.');
            console.error('Add user error:', error);
        }
    };

    const handleDeleteUser  = async (id) => {
        try {
            const response = await fetch(`http://localhost:5000/api/users/delete/${id}`, {
                method: 'DELETE',
            });
            if (!response.ok) {
                throw new Error('Failed to delete user');
            }
            setMessage('User  deleted successfully!');
            fetchUsers(); // Refresh the user list
        } catch (error) {
            setMessage('Error deleting user.');
            console.error('Delete user error:', error);
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">User  Management</h2>
            {message && <div className="alert alert-info">{message}</div>}
            <form onSubmit={handleAddUser }>
                <div className="form-group">
                    <label htmlFor="voterId">Voter ID</label>
                    <input
                        type="text"
                        className="form-control"
                        id="voterId"
                        value={voterId}
                        onChange={(e) => setVoterId(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="image">Image</label>
                    <input
                        type="file"
                        className="form-control"
                        id="image"
                        onChange={(e) => setImage(e.target.files[0])}
                    />
                </div>
                <button type="submit" className="btn btn-success btn-block">
                    Add User
                </button>
            </form>
            <h3 className="mt-4">Existing Users</h3>
            <ul className="list-group">
                {users.map(user => (
                    <li key={user.id} className="list-group-item d-flex justify-content-between align-items-center">
                        {user.voter_id}
                        <button className="btn btn-danger" onClick={() => handleDeleteUser (user.id)}>
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserManagement;
