import React, { useState, useEffect } from "react";
import axios from "axios";
import "./VoterDashboard.css";

const API_URL = "http://localhost:5000/api";

const VoterDashboard = () => {
  const [candidates, setCandidates] = useState([]);
  const [hasVoted, setHasVoted] = useState(false);
  const [isVoting, setIsVoting] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // ‚úÖ Get voter_id from localStorage
  const voter_id = localStorage.getItem("voter_id");

  useEffect(() => {
    if (!voter_id) {
      setError("‚ùå Voter ID is required.");
      return;
    }

    const fetchData = async () => {
      try {
        const [candidatesRes, voterStatusRes] = await Promise.all([
          axios.get(`${API_URL}/candidates`),
          axios.post(`${API_URL}/check-voter`, { voter_id }),
        ]);

        if (candidatesRes.data.success) {
          setCandidates(candidatesRes.data.candidates || []);
        } else {
          setError("‚ùå Failed to load candidates.");
        }

        if (voterStatusRes.data.success === false) {
          setHasVoted(true);
          setSuccessMessage("‚úÖ You have already voted. Thank you!");
        }
      } catch (error) {
        console.error("‚ùå Error loading data:", error);
        setError("‚ùå Failed to load data. Please refresh.");
      }
    };

    fetchData();
  }, [voter_id]);

  const handleVote = async (candidateId, candidateName) => {
    if (hasVoted || isVoting) return;

    setIsVoting(true);
    setError("");
    setSuccessMessage("");

    try {
      const voteRes = await axios.post(`${API_URL}/vote`, { voter_id, candidateName });
      const sheetRes = await axios.post(`${API_URL}/updateGoogleSheets`, { candidateName });

      if (voteRes.data.success && sheetRes.data.success) {
        setCandidates((prev) =>
          prev.map((c) =>
            c._id === candidateId ? { ...c, votes: (c.votes || 0) + 1 } : c
          )
        );

        setHasVoted(true);
        setSuccessMessage(`‚úÖ Your vote for ${candidateName} has been recorded successfully!`);
      } else {
        setError("‚ùå Voting failed. Please try again.");
      }
    } catch (error) {
      console.error("‚ùå Error voting:", error);
      setError("‚ùå Failed to cast vote. Try again.");
    } finally {
      setIsVoting(false);
    }
  };

  return (
    <div className="voter-container">
      <h1 className="title_1">üó≥Ô∏è Online Voting System</h1>

      {error && <p className="error-message">{error}</p>}
      {successMessage && <p className="success-message">{successMessage}</p>}

      <div className="candidate-list">
        {candidates.length > 0 ? (
          candidates.map((candidate) => (
            <div key={candidate._id} className="candidate-row">
              <div className="candidate-info">
                <img
                  src={`http://localhost:5000/uploads/${candidate.logo}`}
                  alt={candidate.name}
                  className="candidate-logo"
                />
                <p className="candidate-name">{candidate.name}</p>
                <p className="vote-count">Votes: {candidate.votes || 0}</p>
              </div>

              <button
                className={`vote-button ${hasVoted ? "disabled" : ""}`}
                onClick={() => handleVote(candidate._id, candidate.name)}
                disabled={hasVoted || isVoting}
              >
                {hasVoted ? "‚úÖ Voted" : isVoting ? "Voting..." : "üó≥Ô∏è Vote"}
              </button>
            </div>
          ))
        ) : (
          <p className="no-candidates">‚ö†Ô∏è No candidates available.</p>
        )}
      </div>
    </div>
  );
};

export default VoterDashboard;
