import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';
import axios from 'axios';
import Webcam from 'react-webcam';

const Login = () => {
    const [id, setId] = useState('');
    const [error, setError] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate();
    const webcamRef = useRef(null); // Reference for the webcam

    const handleLogin = async (e) => {
        e.preventDefault();

        if (id === '8143796138') {
            navigate('/admin-dashboard');
        } else {
            try {
                const response = await axios.post("http://localhost:5000/api/auth/login", { voter_id: id });

                if (response.data.success) {
                    setIsLoggedIn(true); // Set logged in state
                } else {
                    setError(response.data.message);
                }
            } catch (error) {
                setError("Authentication failed. Please try again.");
                console.error("Login error:", error);
            }
        }
    };

    const captureAndCompare = async () => {
        const imageSrc = webcamRef.current.getScreenshot(); // Capture image from webcam

        try {
            const response = await axios.post("http://localhost:5000/api/auth/face-recognition", { image: imageSrc, voter_id: id });

            if (response.data.success) {
                navigate("/voter-dashboard"); // Redirect to voter dashboard on success
            } else {
                setError(response.data.message);
            }
        } catch (error) {
            setError("Face recognition failed. Please try again.");
            console.error("Face recognition error:", error);
        }
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-group">
                        <input
                            type="text"
                            placeholder="Enter ID"
                            value={id}
                            onChange={(e) => setId(e.target.value)}
                            required
                        />
                    </div>
                    {error && <div className="error-message">{error}</div>}
                    <button type="submit" className="login-button">
                        Login
                    </button>
                </form>
            </div>

            {isLoggedIn && (
                <div className="webcam-container">
                    <h2>Face Recognition</h2>
                    <Webcam
                        audio={false}
                        ref={webcamRef}
                        screenshotFormat="image/jpeg"
                        width={400}
                    />
                    <button onClick={captureAndCompare} className="capture-button">
                        Capture and Compare
                    </button>
                </div>
            )}
        </div>
    );
};

export default Login;
