import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CandidateManagement.css'; // Import the CSS file for styling

const CandidateManagement = () => {
  const [candidates, setCandidates] = useState([]);
  const [name, setName] = useState('');
  const [party, setParty] = useState('');
  const [logo, setLogo] = useState(null);
  const [message, setMessage] = useState('');

  // For updating a candidate
  const [editCandidateId, setEditCandidateId] = useState(null);
  const [editName, setEditName] = useState('');
  const [editParty, setEditParty] = useState('');
  const [editLogo, setEditLogo] = useState(null);

  useEffect(() => {
    fetchCandidates();
  }, []);

  const fetchCandidates = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/candidates');
      setCandidates(response.data);
    } catch (error) {
      console.error('Error fetching candidates:', error);
      setMessage('Error fetching candidates.');
    }
  };

  const handleAddCandidate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('party', party);
    if (logo) {
      formData.append('logo', logo);
    }

    try {
      await axios.post('http://localhost:5000/api/candidates', formData);
      setMessage('Candidate added successfully!');
      fetchCandidates();
      setName('');
      setParty('');
      setLogo(null);
    } catch (error) {
      console.error('Error adding candidate:', error);
      setMessage('Error adding candidate.');
    }
  };

  const handleDeleteCandidate = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/candidates/${id}`);
      setMessage('Candidate deleted successfully!');
      fetchCandidates();
    } catch (error) {
      console.error('Error deleting candidate:', error);
      setMessage('Error deleting candidate.');
    }
  };

  const handleEditClick = (candidate) => {
    setEditCandidateId(candidate._id);
    setEditName(candidate.name);
    setEditParty(candidate.party);
    setEditLogo(null);
  };

  const handleCancelEdit = () => {
    setEditCandidateId(null);
    setEditName('');
    setEditParty('');
    setEditLogo(null);
  };

  const handleUpdateCandidate = async (e) => {
    e.preventDefault();

    if (!editCandidateId) {
      setMessage('No candidate selected to update.');
      return;
    }

    const formData = new FormData();
    formData.append('name', editName);
    formData.append('party', editParty);
    if (editLogo) {
      formData.append('logo', editLogo);
    }

    try {
      await axios.put(`http://localhost:5000/api/candidates/${editCandidateId}`, formData);
      setMessage('Candidate updated successfully!');
      fetchCandidates();
      handleCancelEdit();
    } catch (error) {
      console.error('Error updating candidate:', error);
      setMessage('Error updating candidate.');
    }
  };

  return (
    <div className="candidate-management">
      <h2>Candidate Management</h2>
      {message && <div className="message">{message}</div>}

      <form onSubmit={handleAddCandidate}>
        <div className="form-group">
          <input type="text" placeholder="Candidate Name" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div className="form-group">
          <input type="text" placeholder="Party Name" value={party} onChange={(e) => setParty(e.target.value)} required />
        </div>
        <div className="form-group">
          <input type="file" accept="image/*" onChange={(e) => setLogo(e.target.files[0])} />
        </div>
        <button type="submit" className="add-candidate-button">Add Candidate</button>
      </form>

      <h3>Candidate List</h3>
      <div className="candidate-list">
        {candidates.map((candidate) => (
          <div key={candidate._id} className="candidate-card">
            {candidate.logo ? (
              <img src={`http://localhost:5000/uploads/${candidate.logo}`} alt="Candidate Logo" className="candidate-logo" />
            ) : (
              <img src="http://localhost:5000/uploads/default.jpg" alt="Default" className="candidate-logo" />
            )}
            <h4>{candidate.name}</h4>
            <p>{candidate.party}</p>
            <button className="delete-button" onClick={() => handleDeleteCandidate(candidate._id)}>Delete</button>
            <button className="update-button" onClick={() => handleEditClick(candidate)}>Update</button>
          </div>
        ))}
      </div>

      {editCandidateId && (
        <div className="update-form">
          <h3>Update Candidate</h3>
          <form onSubmit={handleUpdateCandidate}>
            <div className="form-group">
              <label>Name:</label>
              <input type="text" value={editName} onChange={(e) => setEditName(e.target.value)} required />
            </div>
            <div className="form-group">
              <label>Party:</label>
              <input type="text" value={editParty} onChange={(e) => setEditParty(e.target.value)} required />
            </div>
            <div className="form-group">
              <label>New Logo (optional):</label>
              <input type="file" accept="image/*" onChange={(e) => setEditLogo(e.target.files[0])} />
            </div>
            <div className="button-container">
              <button type="submit" className="save-button">Save</button>
              <button type="button" className="cancel-button" onClick={handleCancelEdit}>Cancel</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default CandidateManagement;
