import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Candidate.css";

const CandidateManagement = () => {
    const [candidates, setCandidates] = useState([]);
    const [name, setName] = useState("");
    const [party, setParty] = useState("");
    const [logo, setLogo] = useState(null);
    const [editingCandidate, setEditingCandidate] = useState(null);
    const [message, setMessage] = useState("");

    useEffect(() => {
        fetchCandidates();
    }, []);

    const fetchCandidates = async () => {
        try {
            const response = await axios.get("http://localhost:5000/api/candidates");
            setCandidates(response.data);
        } catch (error) {
            console.error("Error fetching candidates:", error);
            setMessage("Error fetching candidates.");
        }
    };

    const handleFileChange = (event) => {
        setLogo(event.target.files[0]);
    };

    const handleAddCandidate = async (event) => {
        event.preventDefault();
        if (!name || !party) {
            alert("Please fill all fields.");
            return;
        }

        const formData = new FormData();
        formData.append("name", name);
        formData.append("party", party);
        if (logo) formData.append("logo", logo);

        try {
            await axios.post("http://localhost:5000/api/candidates/addCandidate", formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            setMessage("Candidate added successfully!");
            fetchCandidates();
            setName("");
            setParty("");
            setLogo(null);
        } catch (error) {
            console.error("Error adding candidate:", error);
            setMessage("Error adding candidate. Please try again.");
        }
    };

    const handleUpdateCandidate = async (event) => {
        event.preventDefault();
        if (!name || !party) {
            alert("Please fill all fields.");
            return;
        }

        const formData = new FormData();
        formData.append("name", name);
        formData.append("party", party);
        if (logo) formData.append("logo", logo);

        try {
            await axios.put(`http://localhost:5000/api/candidates/update/${editingCandidate._id}`, formData, {
                headers: { "Content-Type": "multipart/form-data" },
            });
            setMessage("Candidate updated successfully!");
            fetchCandidates();
            setEditingCandidate(null);
            setName("");
            setParty("");
            setLogo(null);
        } catch (error) {
            console.error("Error updating candidate:", error);
            setMessage("Error updating candidate. Please try again.");
        }
    };

    const handleDeleteCandidate = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/candidates/delete/${id}`);
            fetchCandidates();
            setMessage("Candidate deleted successfully!");
        } catch (error) {
            console.error("Error deleting candidate:", error);
        }
    };

    const handleEditCandidate = (candidate) => {
        setEditingCandidate(candidate);
        setName(candidate.name);
        setParty(candidate.party);
        setLogo(null);
    };

    const handleVote = async (candidateName) => {
        try {
            const response = await axios.post("http://localhost:5000/api/vote", { candidateName });
            if (response.data.success) {
                setMessage(`Vote recorded for ${candidateName}!`);
                fetchCandidates();
            } else {
                setMessage("Failed to cast vote. Try again.");
            }
        } catch (error) {
            console.error("Error casting vote:", error);
            setMessage("Error casting vote. Try again.");
        }
    };

    return (
        <div className="candidate-management_2">
            <h2 className="title_2">Candidate Management</h2>
            {message && <div className="message">{message}</div>}

            <form onSubmit={editingCandidate ? handleUpdateCandidate : handleAddCandidate}>
                <input type="text" placeholder="Candidate Name" value={name} onChange={(e) => setName(e.target.value)} required />
                <input type="text" placeholder="Party Name" value={party} onChange={(e) => setParty(e.target.value)} required />
                <input type="file" accept="image/*" onChange={handleFileChange} />
                <button type="submit">{editingCandidate ? "Update Candidate" : "Add Candidate"}</button>
            </form>

            <h3>Candidate List</h3>
            <ul>
                {candidates.map((candidate) => (
                    <li key={candidate._id}>
                        <img
                            src={`http://localhost:5000/uploads/${candidate.logo}`}
                            alt={candidate.name}
                            onError={(e) => (e.target.src = "/default-logo.png")}
                        />
                        <h4>{candidate.name}</h4>
                        <p>{candidate.party}</p>
                        <p>Votes: {candidate.voteCount}</p>
                        <button onClick={() => handleVote(candidate.name)}>Vote</button>
                        <button onClick={() => handleEditCandidate(candidate)}>Edit</button>
                        <button onClick={() => handleDeleteCandidate(candidate._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CandidateManagement;
