import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const API_URL = "http://localhost:5000/api/auth";

export default function Login() {
  const [voterId, setVoterId] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      if (voterId === "8143796138") {
        // If voter ID is admin's ID, navigate to admin dashboard
        sessionStorage.setItem("admin_id", voterId);
        navigate("/admin-dashboard");
      } else {
        // Otherwise, proceed with face recognition for voters
        const res = await axios.post(`${API_URL}/face-recognition`, { voter_id: voterId });

        if (res.data.success) {
          alert("Face recognized successfully!");

          // Store voter ID in session storage
          sessionStorage.setItem("voter_id", voterId);
          
          // Redirect to the voter dashboard
          navigate("/voter-dashboard");
        } else {
          alert("Face recognition failed. Try again.");
        }
      }
    } catch (error) {
      console.error("Login error:", error);
      alert("Error logging in. Check console for details.");
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h1 className="text-2xl font-bold mb-4">Voter Login</h1>
      <input
        type="text"
        placeholder="Enter Voter ID"
        value={voterId}
        onChange={(e) => setVoterId(e.target.value)}
        className="border p-2 w-full mb-2"
        required
      />
      <button onClick={handleLogin} className="bg-blue-500 text-white p-2 w-full">
        Login
      </button>
    </div>
  );
}
