import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

const VoterDashboard = () => {
  const voter_id = "456789123025"; // Hardcoded voter ID
  const [candidates, setCandidates] = useState([]);
  const [hasVoted, setHasVoted] = useState(false);
  const [voting, setVoting] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Fetch candidates and voter status
  useEffect(() => {
    if (!voter_id) return; // Prevent API call if voter_id is undefined

    const fetchData = async () => {
      try {
        console.log("Fetching candidates and voter status...");

        const [candidatesRes, voterStatusRes] = await Promise.all([
          axios.get(`${API_URL}/candidates`),
          axios.post(`${API_URL}/voter/check-voter`, { voter_id }, {
            headers: { "Content-Type": "application/json" },
          }),
        ]);

        console.log("Candidates Response:", candidatesRes.data);
        console.log("Voter Status Response:", voterStatusRes.data);

        if (candidatesRes.data.success) {
          setCandidates(candidatesRes.data.candidates || []);
        } else {
          setError("‚ùå Failed to load candidates.");
        }

        if (!voterStatusRes.data.success) {
          setHasVoted(true);
          setSuccessMessage("‚úÖ You have already voted. Thank you!");
        }
      } catch (error) {
        console.error("‚ùå Error fetching data:", error);
        setError("‚ùå Failed to load data. Please refresh.");
      }
    };

    fetchData();
  }, []);

  // Handle voting
  const handleVote = async (candidateId) => {
    if (hasVoted || voting) return;

    setVoting(true);
    setError("");
    setSuccessMessage("");

    try {
      console.log("Submitting vote for:", candidateId);

      const response = await axios.post(`${API_URL}/vote`, {
        voter_id,
        candidate_id: candidateId,
      });

      console.log("Vote Response:", response.data);

      if (response.data.success) {
        setHasVoted(true);
        setSuccessMessage("üéâ Your vote has been successfully recorded!");
      } else {
        setError(response.data.message);
      }
    } catch (error) {
      console.error("‚ùå Error submitting vote:", error);
      setError("‚ùå Failed to submit vote. Please try again.");
    } finally {
      setVoting(false);
    }
  };

  return (
    <div>
      <h2>üó≥ Voter Dashboard</h2>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {successMessage && <p style={{ color: "green" }}>{successMessage}</p>}

      {hasVoted ? (
        <p>‚úÖ You have already voted. Thank you for participating!</p>
      ) : (
        <div>
          <h3>Candidates</h3>
          {candidates.length > 0 ? (
            candidates.map((candidate, index) => (
              <div key={candidate._id || index} className="candidate-row">
                <div className="candidate-info">
                  <img
                    src={`http://localhost:5000/uploads/${candidate.logo}`}
                    alt={candidate.name}
                    className="candidate-logo"
                  />
                  <p className="candidate-name">{candidate.name}</p>
                  <p className="vote-count">Votes: {candidate.voteCount ?? 0}</p>
                  <button
                    className="vote-button"
                    onClick={() => handleVote(candidate._id)}
                    disabled={voting}
                  >
                    {voting ? "Voting..." : "üó≥Ô∏è Vote"}
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p className="no-candidates">‚ö†Ô∏è No candidates available.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default VoterDashboard;
